import studentForm from "../images/student_Form.jpeg";
import { styled } from "@mui/material/styles";

const StudentById = () => {
  const [data, setData] = useState([]);
  const { id } = useParams();
  const [file, setFile] = useState(studentPic);
  const [form, setForm] = useState(studentForm);
  const [editForm, setEditForm] = useState("none");
  const [displayDetails, setDisplayDetail] = useState("block");

  const [studentName, setStudentName] = useState("");
  const [address, setAddress] = useState("");
  const [cell, setCell] = useState("");
  const [branch, setBranch] = useState("");
  const [mail, setEmail] = useState("");
  const [year, setYear] = useState("");
  const [motherName, setMotherName] = useState("");
  const [fatherName, setFatherName] = useState("");
  const [parentCell, setParentCell] = useState("");
  const [lastName, setLastName] = useState("");
  const [joinDate, setDate] = useState("");

  const navigate = useNavigate();

  useEffect(() => {
    async function Res() {
      const result = await fetch(`http://localhost:5555/students/${id}`);
      const res = await result.json();
      setData(res);
    }

    Res();
  });

  const moduleChange = () => {
    setEditForm("block");
    setDisplayDetail("none");
    setStudentName(data.studentName);
    setAddress(data.address);
    setCell(data.cell);
    setBranch(data.branch);
    setEmail(data.email);
    setYear(data.year);
    setMotherName(data.motherName);
    setFatherName(data.fatherName);
    setParentCell(data.parentCell);
    setLastName(data.lastName);
    setDate(data.date);
  };



  const reverseModule = () => {
    setEditForm("none");
    setDisplayDetail("block");
  };

  const DeleteStudent = () => {
    if (window.confirm("Are you want it to delete Permanently?")) {
      fetch(`http://localhost:5555/students/${id}`, {
        method: "DELETE",
      })
        .then((res) => res.json())
        .then((data) => console.log(data))
        .catch((error) => console.error(error));
    }
    navigate("/allstudents");
  };

  const VisuallyHiddenInput = styled("input")({
    clip: "rect(0 0 0 0)",
    clipPath: "inset(50%)",
    height: 1,
    overflow: "hidden",
    position: "absolute",
    bottom: 0,
    left: 0,
    whiteSpace: "nowrap",
    width: 1,
  });

  function handleStudentName(e) {
    setStudentName(e.target.value);
  }

  function handleAddress(e) {
    setAddress(e.target.value);
  }

  function handleCell(e) {
    setCell(e.target.value);
  }

  function handleBranch(e) {
    setBranch(e.target.value);
  }

  function handleEmail(e) {
    setEmail(e.target.value);
  }

  function handleYear(e) {
    setYear(e.target.value);
  }

  function handleMotherName(e) {
    setMotherName(e.target.value);
  }

  function handleFatherName(e) {
    setFatherName(e.target.value);
  }

  function handleParentCell(e) {
    setParentCell(e.target.value);
  }

  function handleLastName(e) {
    setLastName(e.target.value);
  }

  function handleDate(e) {
    setDate(e.target.value);
  }

  function handleChange(e) {
    var reader = new FileReader();
    reader.readAsDataURL(e.target.files[0]);
    reader.onload = () => {
      setFile(reader.result);
    };
    reader.onerror = (error) => {
      console.log(error);
    };
  }

  function handleFormChange(e) {
    var reader = new FileReader();
    reader.readAsDataURL(e.target.files[0]);
    reader.onload = () => {
      setForm(reader.result);
    };
    reader.onerror = (error) => {
      console.log(error);
    };
  }

  return (
    <div>
      <div style={{ display: editForm }} className="updateStudent">
        <div style={{ display: "flex", marginBottom: "3%" }}>
          <Button
            onClick={reverseModule}
            style={{
              backgroundColor: "red",
              color: "blue",
              fontWeight: "700",
              padding: "5px",
              marginLeft: "0px",
              marginRight: "5%",
              border: "1px dotted yellow",
            }}
          >
            Cancel
          </Button>
          <h3>Update Students Details !</h3>
        </div>
        <div className="updateImgInfo">
          <form>
            <TextField
              value={studentName}
              onChange={handleStudentName}
              fullWidth
              label="Student Name"
              id="fullWidth"
              margin="normal"
            />
            <TextField
              value={lastName}
              onChange={handleLastName}
              fullWidth
              label="Last Name"
              id="fullWidth"
              margin="normal"
            />
            <TextField
              fullWidth
              value={address}
              onChange={handleAddress}
              label="Address"
              id="fullWidth"
              margin="normal"
            />
            <TextField
              fullWidth
              value={mail}
              onChange={handleEmail}
              label="Email"
              id="fullWidth"
              margin="normal"
            />
            <TextField
              fullWidth
              value={cell}
              onChange={handleCell}
              type="number"
              label="Mobile No."
              id="fullWidth"
              margin="normal"
            />
            <TextField
              fullWidth
              value={joinDate}
              onChange={handleDate}
              type="date"
              label="Date of Joining"
              id="fullWidth"
              margin="normal"
            />
            <TextField
              fullWidth
              value={branch}
              onChange={handleBranch}
              label="Branch"
              id="fullWidth"
              margin="normal"
            />
            <TextField
              fullWidth
              value={year}
              onChange={handleYear}
              type="number"
              label="Year"
              id="fullWidth"
              margin="normal"
            />
            <TextField
              fullWidth
              value={fatherName}
              onChange={handleFatherName}
              label="Father Name"
              id="fullWidth"
              margin="normal"
            />
            <TextField
              fullWidth
              value={motherName}
              onChange={handleMotherName}
              label="Mother Name"
              id="fullWidth"
              margin="normal"
            />
            <TextField
              fullWidth
              value={parentCell}
              onChange={handleParentCell}
              type="number"
              label="Parents Contact No."
              id="fullWidth"
              margin="normal"
            />
            <div className="studentImgBtnDiv">
              <Button
                onChange={handleChange}
                component="label"
                variant="contained"
                color="success"
                startIcon={<CloudUploadIcon />}
              >
                Replace Student Image
                <VisuallyHiddenInput type="file" />
              </Button>

              <Button
                onChange={handleFormChange}
                component="label"
                variant="contained"
                color="success"
                startIcon={<CloudUploadIcon />}
              >
                Replace Form
                <VisuallyHiddenInput type="file" />
              </Button>
            </div>
            <div className="addbookbtn">
              <Button
                onClick={StudentUpdate}
                fullWidth
                className="btn_In"
                variant="contained"
                size="large"
                endIcon={<SendIcon />}
              >
                Update Student Details
              </Button>
            </div>
          </form>
          <div className="updateStudentImg">
            <img src={file} className="updateStudentImage" alt="student" />
            <img src={form} className="updateFormImage" alt="form" />
          </div>
        </div>
      </div>

      <div style={{ display: displayDetails }} className="singleStudentBlock">
        <div className="btnBlock">
          <button className="btngreen" onClick={moduleChange}>
            Edit
          </button>
          <button className="btnred" onClick={DeleteStudent}>
            Delete
          </button>
        </div>
        <div className="studentbyid">
          <img src={studentPic} alt="Student Imgage" />
          <div className="studentbyidFlex">
            <div>
              <h3>Student Name :</h3>
              <p>{data.studentName + " " + data.lastName}</p>
            </div>
            <div>
              <h3>Year ( Branch ):</h3>
              <p>{data.year + " " + "(" + data.branch + ")"}</p>
            </div>
            <div>
              <h3>Cell :</h3>
              <p>{data.cell}</p>
            </div>
            <div>
              <h3>Email :</h3>
              <p>{data.email}</p>
            </div>
          </div>
          <div className="studentbyidFlex">
            <div>
              <h3>Address :</h3>
              <p>{data.address}</p>
            </div>
            <div>
              <h3>Father Name :</h3>
              <p>{data.fatherName}</p>
            </div>
            <div>
              <h3>Mother Name :</h3>
              <p>{data.motherName}</p>
            </div>
            <div>
              <h3>Parent Cell :</h3>
              <p>{data.parentCell}</p>
            </div>
          </div>
        </div>
      </div>

      <div style={{ display: displayDetails }} className="bookBySingleStudent">
        <table className="table">
          <tr>
            <th>Sr. No.</th>
            <th>Book Name</th>
            <th>Auther</th>
            <th>Edition</th>
            <th>Books Taken</th>
          </tr>
          <tr>
            <td>01</td>
            <td>Vishudhhi Marg</td>
            <td>Budhhaghosh</td>
            <td>1</td>
            <td>01</td>
          </tr>
          <tr>
            <td>02</td>
            <td>Buddha & His Dhamma</td>
            <td>Dr. B R Ambedkar</td>
            <td>5</td>
            <td>01</td>
          </tr>
        </table>
      </div>
    </div>
  );
};

export default StudentById;
