import React from "react";
import Box from '@mui/material/Box';
import TextField from '@mui/material/TextField';

const AddBook = () => {
  const [file, setFile] = useState();
    function handleChange(e) {
        console.log(e.target.files);
        setFile(URL.createObjectURL(e.target.files[0]));
    }
  return (
    <div className="addbook">

       <Box
      sx={{
        width: 500,
        maxWidth: '100%',
        margin: 10,
      }}
    >
      <h3>Fill New Books Details Here!</h3>
      <TextField fullWidth label="Book Name" id="fullWidth" margin="normal"/>
      <TextField fullWidth label="Auther Name" id="fullWidth" margin="normal"/>
      <TextField fullWidth label="Edition" id="fullWidth" margin="normal"/>
      <TextField fullWidth type='number' label="Number of Copies" id="fullWidth" margin="normal"/>
      <TextField fullWidth type='date' label="Available In Library Date" id="fullWidth" margin="dense"/>
      <h2>Add Image:</h2>
            <input type="file" onChange={handleChange} />
            <img src={file} />
    </Box>
    </div>
  );
};

export default AddBook;
