import React, { useState, useEffect } from "react";
import { useNavigate, useParams } from "react-router-dom";
import studentPic from "../images/student_Image.avif";
import TextField from "@mui/material/TextField";
import Button from "@mui/material/Button";
import SendIcon from "@mui/icons-material/Send";
import CloudUploadIcon from "@mui/icons-material/CloudUpload";
import studentForm from "../images/student_Form.jpeg";
import { styled } from "@mui/material/styles";

const StudentById = () => {
  const [data, setData] = useState([]);
  const [updateData, setUpdateData] = useState([]);
  const { id } = useParams();
  // const [formatDate, setformatDate] = useState(studentPic);
  // const [form, setForm] = useState(studentForm);
  const [editForm, setEditForm] = useState("none");
  const [displayDetails, setDisplayDetail] = useState("block");

  const [newData, setNewData] = useState({
    studentImg: studentPic,
    studentForm: studentForm,
    studentName: " ",
    lastName: " ",
    address: " ",
    cell: " ",
    branch: " ",
    email: " ",
    year: " ",
    motherName: " ",
    fatherName: " ",
    parentCell: " ",
    joinDate: " ",
  });

  const navigate = useNavigate();

  useEffect(() => {
    async function Res() {
      const result = await fetch(`http://localhost:5555/students/${id}`);
      const res = await result.json();
      setData(res);
    }

    Res();
  }, [id, updateData]);

  // const joiningDate = new Date(`${data.date}`);
  // const formattedDate = joiningDate.toLocaleDateString("en-GB", {
  //   day: "numeric",
  //   month: "numeric",
  //   year: "numeric",
  // });

  // let formattedDate = moment(data.date).format('dd/mm/yyyy hh:mm');

  const moduleChange = () => {
    setEditForm("block");
    setDisplayDetail("none");
    setNewData({
      studentImg: data.studentImg,
      studentForm: data.studentForm,
      studentName: data.studentName,
      lastName: data.lastName,
      address: data.address,
      cell: data.cell,
      branch: data.branch,
      email: data.email,
      year: data.year,
      motherName: data.motherName,
      fatherName: data.fatherName,
      parentCell: data.parentCell,
      dateOfJoining: data.dateOfJoining,
    });
  };

  const StudentUpdate = (e) => {
    e.preventDefault();
    console.log(newData)
    if (
      !newData.studentName ||
      !newData.address ||
      !newData.cell ||
      !newData.branch ||
      !newData.email ||
      !newData.year ||
      !newData.motherName ||
      !newData.fatherName ||
      !newData.parentCell ||
      !newData.lastName ||
      newData.studentImg == studentPic ||
      newData.studentForm == studentForm
    ) {
      setNewData({...newData, studentImg: studentPic});
      setNewData({...newData, studentForm: studentForm});
      return alert("Fill all required field");
    }

    const requestOptions = {
      method: "PATCH",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(newData),
    };

    fetch(`http://localhost:5555/students/${id}`, requestOptions).then(
      (response) =>
        response.json().then(alert("Student Updated In Library Successfully!"))
    );

    setEditForm("none");
    setDisplayDetail("block");
    setUpdateData(newData);
  };

  const reverseModule = () => {
    setEditForm("none");
    setDisplayDetail("block");
  };

  const DeleteStudent = () => {
    if (window.confirm("Are you want it to delete Permanently?")) {
      fetch(`http://localhost:5555/students/${id}`, {
        method: "DELETE",
      })
        .then((res) => res.json())
        .then((data) => console.log(data))
        .catch((error) => console.error(error));
    }
    navigate("/allstudents");
  };

  const VisuallyHiddenInput = styled("input")({
    clip: "rect(0 0 0 0)",
    clipPath: "inset(50%)",
    height: 1,
    overflow: "hidden",
    position: "absolute",
    bottom: 0,
    left: 0,
    whiteSpace: "nowrap",
    width: 1,
  });

  let name, value;

  const handleInputs = (e) => {
    name = e.target.name;
    if(name !== "studentImg" || name !== "studentForm"){
      value = e.target.value
    }

    setNewData({...newData, [name]: value})
  }

  function handleChange(e) {
    var reader = new FileReader();
    reader.readAsDataURL(e.target.files[0]);
    reader.onload = () => {
      setNewData({...newData, studentImg: reader.result});
    };
    reader.onerror = (error) => {
      console.log(error);
    };
  }

  function handleFormChange(e) {
    var reader = new FileReader();
    reader.readAsDataURL(e.target.files[0]);
    reader.onload = () => {
      setNewData({...newData, studentForm: reader.result});
    };
    reader.onerror = (error) => {
      console.log(error);
    };
  }

  function formatDate({data}) {
    const date = data.dateOfJoining
    date.toISOString().split('T')[0]
  }

  return (
    <div>
      <div style={{ display: editForm }} className="updateStudent">
        <div style={{ display: "flex", marginBottom: "3%" }}>
          <Button
            onClick={reverseModule}
            style={{
              backgroundColor: "red",
              color: "blue",
              fontWeight: "700",
              padding: "5px",
              marginLeft: "0px",
              marginRight: "5%",
              border: "1px dotted yellow",
            }}
          >
            Cancel
          </Button>
          <h3>Update Students Details !</h3>
        </div>
        <div className="updateImgInfo">
          <form>
            <TextField
            name="studentName"
              value={newData.studentName}
              onChange={handleInputs}
              fullWidth
              label="Student Name"
              id="fullWidth"
              margin="normal"
            />
            <TextField
            name="lastName"
              value={newData.lastName}
              onChange={handleInputs}
              fullWidth
              label="Last Name"
              id="fullWidth"
              margin="normal"
            />
            <TextField
              fullWidth
              name="address"
              value={newData.address}
              onChange={handleInputs}
              label="Address"
              id="fullWidth"
              margin="normal"
            />
            <TextField
              fullWidth
              name="email"
              value={newData.email}
              onChange={handleInputs}
              label="Email"
              id="fullWidth"
              margin="normal"
            />
            <TextField
              fullWidth
              name="cell"
              value={newData.cell}
              onChange={handleInputs}
              type="number"
              label="Mobile No."
              id="fullWidth"
              margin="normal"
            />
            <TextField
              fullWidth
              name="dateOfJoining"
              value={newData.dateOfJoining}
              onChange={handleInputs}
              type="date"
              label="Date of Joining"
              id="fullWidth"
              margin="normal"
            />
            <TextField
              fullWidth
              name="branch"
              value={newData.branch}
              onChange={handleInputs}
              label="Branch"
              id="fullWidth"
              margin="normal"
            />
            <TextField
              fullWidth
              name="year"
              value={newData.year}
              onChange={handleInputs}
              type="number"
              label="Year"
              id="fullWidth"
              margin="normal"
            />
            <TextField
              fullWidth
              name="fatherName"
              value={newData.fatherName}
              onChange={handleInputs}
              label="Father Name"
              id="fullWidth"
              margin="normal"
            />
            <TextField
              fullWidth
              name="motherName"
              value={newData.motherName}
              onChange={handleInputs}
              label="Mother Name"
              id="fullWidth"
              margin="normal"
            />
            <TextField
              fullWidth
              name="parentCell"
              value={newData.parentCell}
              onChange={handleInputs}
              type="number"
              label="Parents Contact No."
              id="fullWidth"
              margin="normal"
            />
            <div className="studentImgBtnDiv">
              <Button
                onChange={handleChange}
                component="label"
                variant="contained"
                color="success"
                startIcon={<CloudUploadIcon />}
              >
                Replace Student Image
                <VisuallyHiddenInput type="file" />
              </Button>

              <Button
                onChange={handleFormChange}
                component="label"
                variant="contained"
                color="success"
                startIcon={<CloudUploadIcon />}
              >
                Replace Form
                <VisuallyHiddenInput type="file" />
              </Button>
            </div>
            <div className="addbookbtn">
              <Button
                onClick={StudentUpdate}
                fullWidth
                className="btn_In"
                variant="contained"
                size="large"
                endIcon={<SendIcon />}
              >
                Update Student Details
              </Button>
            </div>
          </form>
          <div className="updateStudentImg">
            <img src={newData.studentImg} className="updateStudentImage" alt="student" />
            <img src={newData.studentForm} className="updateFormImage" alt="form" />
          </div>
        </div>
      </div>

      <div style={{ display: displayDetails }} className="singleStudentBlock">
        <div className="btnBlock">
          <button className="btngreen" onClick={moduleChange}>
            Edit
          </button>
          <button className="btnred" onClick={DeleteStudent}>
            Delete
          </button>
        </div>
        <div className="studentbyid">
          <img src={data.studentImg} alt="Student Imgage" />
          <div className="studentbyidFlex">
            <div>
              <h3>Student Name :</h3>
              <p>{data.studentName + " " + data.lastName}</p>
            </div>
            <div>
              <h3>Year ( Branch ):</h3>
              <p>{data.year + " " + "(" + data.branch + ")"}</p>
            </div>
            <div>
              <h3>Cell :</h3>
              <p>{data.cell}</p>
            </div>
            <div>
              <h3>Email :</h3>
              <p>{data.email}</p>
            </div>
          </div>
          <div className="studentbyidFlex">
            <div>
              <h3>Date Of Join :</h3>
              <p>{data.dateOfJoining}</p>
            </div>
            <div>
              <h3>Address :</h3>
              <p>{data.address}</p>
            </div>
            <div>
              <h3>Father Name :</h3>
              <p>{data.fatherName}</p>
            </div>
            <div>
              <h3>Mother Name :</h3>
              <p>{data.motherName}</p>
            </div>
            <div>
              <h3>Parent Cell :</h3>
              <p>{data.parentCell}</p>
            </div>
          </div>
        </div>
      </div>

      <div style={{ display: displayDetails }} className="bookBySingleStudent">
        <table className="table">
          <tr>
            <th>Sr. No.</th>
            <th>Book Name</th>
            <th>Auther</th>
            <th>Edition</th>
            <th>Books Taken</th>
          </tr>
          <tr>
            <td>01</td>
            <td>Vishudhhi Marg</td>
            <td>Budhhaghosh</td>
            <td>1</td>
            <td>01</td>
          </tr>
          <tr>
            <td>02</td>
            <td>Buddha & His Dhamma</td>
            <td>Dr. B R Ambedkar</td>
            <td>5</td>
            <td>01</td>
          </tr>
        </table>
      </div>
    </div>
  );
};

export default StudentById;
