  //   }
  // }

  // function handleBookName(e) {
  //   setBookName(e.target.value);
  // }

  // function handleAutherName(e) {
  //   setAutherName(e.target.value);
  // }

  // function handleEdition(e) {
  //   setEdition(e.target.value);
  // }

  // function handleNumberCopies(e) {
  //   setNumberCopies(e.target.value);
  // }

  // function handleDate(e) {
  //   setDate(e.target.value);
  // }

  const navigate = useNavigate();

  let name, value;

  const handleInputs = (e) => {
    if(name !== "file"){}
    name = e.target.name;
    value = e.target.value || e.target.file[0];

    setData({ ...data, [name]: value });
  };

  const NavigateToMainPage = async (e) => {
    e.preventDefault();
    console.log(data);

    // if (
    //   !bookName ||
    //   !autherName ||
    //   !edition ||
    //   !date ||
    //   !numberCopies ||
    //   file === logo
    // ) {
    //   setFile(logo);
