    // setFile(logo);
    // setBookName("");
    // setAutherName("");
    // setEdition("");
    // setNumberCopies("");
    // setDate("");
    // navigate("/allbooks");
  };

  const VisuallyHiddenInput = styled("input")({
    clip: "rect(0 0 0 0)",
    clipPath: "inset(50%)",
    height: 1,
    overflow: "hidden",
    position: "absolute",
    bottom: 0,
    left: 0,
    whiteSpace: "nowrap",
    width: 1,
  });

  return (
    <form className="addbook" encType="multipart/form-data">
      <Box
        sx={{
          width: 500,
          maxWidth: "100%",
          margin: 10,
        }}
      >
        <h3>Fill New Books Details Here!</h3>
        <TextField
          name="bookName"
          value={data.bookName}
          // onChange={handleBookName}
          onChange={handleInputs}
          fullWidth
          label="Book Name"
          id="fullWidth"
          margin="normal"
        />
        <TextField
          name="autherName"
          value={data.autherName}
          // onChange={handleAutherName}
          onChange={handleInputs}
          fullWidth
          label="Auther Name"
          id="fullWidth"
          margin="normal"
        />
        <TextField
          name="edition"
          value={data.edition}
          // onChange={handleEdition}
          onChange={handleInputs}
          fullWidth
          type="number"
          label="Edition"
          id="fullWidth"
          margin="normal"
        />
        <TextField
          name="numberCopies"
          value={data.numberCopies}
          // onChange={handleNumberCopies}
          onChange={handleInputs}
          fullWidth
          type="number"
          label="Number of Copies"
          id="fullWidth"
          margin="normal"
        />
        <TextField
          name="date"
          value={data.date}
          // onChange={handleDate}
          onChange={handleInputs}
          fullWidth
          type="date"
          label="Available In Library Date"
          id="fullWidth"
          margin="normal"
        />
        <Button
