import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import logo from "../images/hand-book-logo-illustration-art-background-43965136.webp";
import TextField from "@mui/material/TextField";
import Button from "@mui/material/Button";
import SendIcon from "@mui/icons-material/Send";
import CloudUploadIcon from "@mui/icons-material/CloudUpload";
import { styled } from "@mui/material/styles";

const BookById = () => {
  const [data, setData] = useState([]);
  const { id } = useParams();
  const [editForm, setEditForm] = useState("none");
  const [displayDetails, setDisplayDetail] = useState("flex");

  useEffect(() => {
    async function Res() {
      const result = await fetch("http://localhost:5555/books");
      const res = await result.json();
      setData(res.data);
    }

    Res();
  });

  const moduleChange = () => {
    setEditForm("block");
    setDisplayDetail("none");
  };

  const reverseModule = () => {
    setEditForm("none");
    setDisplayDetail("flex");
  };

  const VisuallyHiddenInput = styled("input")({
    clip: "rect(0 0 0 0)",
    clipPath: "inset(50%)",
    height: 1,
    overflow: "hidden",
    position: "absolute",
    bottom: 0,
    left: 0,
    whiteSpace: "nowrap",
    width: 1,
  });


  return (
    <div>
      <div style={{ display: editForm }} className="updateStudent">
        <div style={{ display: "flex", "margin-bottom": "3%" }}>
          <Button
            onClick={reverseModule}
            style={{
              backgroundColor: "red",
              color: "blue",
              fontWeight: "700",
              padding: "5px",
              "margin-left": "0px",
              "margin-right": "5%",
              border: "1px dotted yellow",
            }}
          >
            Cancel
          </Button>
          <h3>Update Students Details !</h3>
        </div>
        <div className="updateImgInfo">
          <form>
            <TextField
              // value={studentName}
              // onChange={handleStudentName}
              fullWidth
              label="Student Name"
              id="fullWidth"
              margin="normal"
            />
            <TextField
              // value={lastName}
              // onChange={handleLastName}
              fullWidth
              label="Last Name"
              id="fullWidth"
              margin="normal"
            />
            <TextField
              fullWidth
              // value={address}
              // onChange={handleAddress}
              label="Address"
              id="fullWidth"
              margin="normal"
            />
            <TextField
              fullWidth
              // value={mail}
              // onChange={handleEmail}
              label="Email"
              id="fullWidth"
              margin="normal"
            />
            <TextField
              fullWidth
              // value={cell}
              // onChange={handleCell}
              type="number"
              label="Mobile No."
              id="fullWidth"
              margin="normal"
            />
            <TextField
              fullWidth
              // value={joinDate}
              // onChange={handleDate}
              type="date"
              label="Date of Joining"
              id="fullWidth"
              margin="normal"
            />
            <TextField
              fullWidth
              // value={branch}
              // onChange={handleBranch}
              label="Branch"
              id="fullWidth"
              margin="normal"
            />
            <TextField
              fullWidth
              // value={year}
              // onChange={handleYear}
              type="number"
              label="Year"
              id="fullWidth"
              margin="normal"
            />
            <TextField
              fullWidth
              // value={fatherName}
              // onChange={handleFatherName}
              label="Father Name"
              id="fullWidth"
              margin="normal"
            />
            <TextField
              fullWidth
              // value={motherName}
              // onChange={handleMotherName}
              label="Mother Name"
              id="fullWidth"
              margin="normal"
            />
            <TextField
              fullWidth
              // value={parentCell}
              // onChange={handleParentCell}
              type="number"
              label="Parents Contact No."
              id="fullWidth"
              margin="normal"
            />
            <div className="studentImgBtnDiv">
              <Button
                // onChange={handleChange}
                component="label"
                variant="contained"
                color="success"
                startIcon={<CloudUploadIcon />}
              >
                Replace Student Image
                <VisuallyHiddenInput type="file" />
              </Button>

              <Button
                // onChange={handleFormChange}
                component="label"
                variant="contained"
                color="success"
                startIcon={<CloudUploadIcon />}
              >
                Replace Form
                <VisuallyHiddenInput type="file" />
              </Button>
            </div>
            <div className="addbookbtn">
              <Button
                // onClick={NavigateToMainPage}
                fullWidth
                className="btn_In"
                variant="contained"
                size="large"
                endIcon={<SendIcon />}
              >
                Update Student Details
              </Button>
            </div>
          </form>
          <div className="updateStudentImg">
            <img src={logo} className="updateFormImage" alt="form" />
          </div>
        </div>
      </div>
      {data.map((ele, ind) => {
        if (ele._id === id) {
          return (
            <div style={{ display: displayDetails }} key={ind} className="bookbyid">
              <img src={logo} alt="book Imgage" />
              <div className="bookbyidFlex">
                <div>
                  <h3>Book Name :</h3>
                  <p>{ele.bookName}</p>
                </div>
                <div>
