import React, { useState } from "react";
import "../App.css";
import TextField from "@mui/material/TextField";
import Button from "@mui/material/Button";
import logo from "../images/hand-book-logo-illustration-art-background-43965136.webp";
import { useNavigate } from "react-router-dom";
import { Toaster } from "react-hot-toast";
import { useFormik } from "formik";
import { logInValidate } from "../helper/validate";
import { logInValidate1 } from "../helper/validate1";
import { logInValidate2 } from "../helper/validate2";

const Home = () => {
  const [logInBlock, setLoginBlock] = useState("block");
  const [getOtpBlock, setGetOtpBlock] = useState("none");
  const [registerBlock, setRegisterBlock] = useState("none");
  const navigate = useNavigate();

  // const NavigateToMainPage = () => {
  //   navigate("/allbooks");
  // };

  const GetOTPModule = () => {
    setLoginBlock("none");
    setGetOtpBlock("block");
  };
  const RegisterModule = () => {
    setLoginBlock("none");
    setGetOtpBlock("none");
    setRegisterBlock("block");
  };
  const LogInModule = () => {
    setLoginBlock("block");
    setGetOtpBlock("none");
    setRegisterBlock("none");
  };

  const formik = useFormik({
    initialValues: {
      email: "",
      password: "",
    },
    validate: logInValidate,
    validateOnBlur: false,
    validateOnChange: false,
    onSubmit: async (values) => {
      console.log(values)
      const token = await fetch("http://localhost:5555/login/login", {
        method: "POST",
        headers: {'Accept': 'application/json',
    'Content-Type': 'application/json'},
        body: JSON.stringify({ email:values.email }),
      });
      console.log(token);
      navigate("/allbooks");
    },
  });
  const otpformik = useFormik({
    initialValues: {
      name: "",
      email: "",
      mobile: "",
      password: "",
      conpassword: "",
    },
    validate: logInValidate1,
    validateOnBlur: false,
    validateOnChange: false,
    onSubmit: async (values) => {
      const mailOtp = await fetch(
        "http://localhost:5555/login/generateOTP/titarekunal@gmail.com",
        { method: "GET", headers: { "Content-Type": "application/json" } }
      );
      const otp = await mailOtp.json();
      console.log(otp);
      fetch("http://localhost:5555/login/registerMail", {
        method: "POST",
        mode: "no-cors",
        body: { otp: `${otp.code}` },
        headers: { "Content-Type": "application/json" },
      });
      RegisterModule();
    },
  });
  const registerformik = useFormik({
    initialValues: {
      otp: "",
    },
    validate: logInValidate2,
    validateOnBlur: false,
    validateOnChange: false,
    onSubmit: async (values) => {
      // fetch(`http://localhost:5555/login/verifyOTP/${values.otp}`)
    },
  });

  return (
    <div className="home">
      <img className="homeLogo" src={logo} alt="library logo" />
      <Toaster></Toaster>
      <div className="homeFirstDiv" style={{ display: logInBlock }}>
        <p className="welcome">WELCOME - TO - LIBRARY</p>
        <p className="slok">
          I measure the progress of a community by the degree of progress which
          women have achieved... Dr. B R Ambedkar
        </p>
        <form onSubmit={formik.handleSubmit}>
          <div className="homeFirstDiv_childFirstDiv">
            <div className="textfield">
              <TextField
                {...formik.getFieldProps("email")}
                fullWidth
                label="Email"
                id="fullWidth"
              />
            </div>
            <div className="textfield">
              <TextField
                {...formik.getFieldProps("password")}
                fullWidth
                label="Password"
                type="password"
                id="fullWidth"
              />
            </div>
          </div>
          <Button
            // onClick={NavigateToMainPage}
            type="submit"
            fullWidth
            className="btn_In"
            variant="contained"
          >
            Sign-In
          </Button>
        </form>
        <p className="or">Forgate Password ?</p>
        <p className="or" onClick={GetOTPModule}>
          New Here ?
        </p>
      </div>
      <div className="homeSecondDiv" style={{ display: getOtpBlock }}>
        <p className="welcome">WELCOME - TO - LIBRARY</p>
        <form onSubmit={otpformik.handleSubmit}>
          <div className="homeFirstDiv_childFirstDiv">
            <div className="textfield">
              <TextField
                {...otpformik.getFieldProps("name")}
                fullWidth
                label="Name"
                id="fullWidth"
              />
            </div>
            <div className="textfield">
              <TextField
                {...otpformik.getFieldProps("email")}
                fullWidth
                label="Email"
                id="fullWidth"
              />
            </div>
            <div className="textfield">
              <TextField
                {...otpformik.getFieldProps("mobile")}
                fullWidth
                label="Mobile No."
                id="fullWidth"
              />
            </div>
            <div className="textfield">
              <TextField
                {...otpformik.getFieldProps("password")}
                fullWidth
                label="Password"
                type="password"
                id="fullWidth"
              />
            </div>
            <div className="textfield">
              <TextField
                {...otpformik.getFieldProps("conpassword")}
                fullWidth
                label="Confirm Password"
                type="password"
                id="fullWidth"
              />
            </div>
          </div>
          <Button
            // onClick={RegisterModule}
            type="submit"
            fullWidth
            className="btn_In"
            variant="contained"
          >
            Get OTP
          </Button>
        </form>
        <p className="or" onClick={LogInModule}>
          ---Back to Log In
        </p>
      </div>
      <div className="homeThirdDiv" style={{ display: registerBlock }}>
        <p className="welcome">WELCOME - TO - LIBRARY</p>
        <form onSubmit={registerformik.handleSubmit}>
          <div className="homeFirstDiv_childFirstDiv">
            <div className="textfield">
              <TextField
                {...registerformik.getFieldProps("otp")}
                fullWidth
                label="Verify OTP"
                id="fullWidth"
              />
            </div>
            <p className="or">Resend OTP ?</p>
          </div>
          <Button
            // onClick={NavigateToMainPage}
            type="submit"
            fullWidth
            className="btn_In"
            variant="contained"
            style={{ marginTop: "2%" }}
          >
            Register
          </Button>
        </form>
        <p className="or" onClick={LogInModule}>
          ---Back to Log In
        </p>
      </div>
    </div>
  );
};

export default Home;
