import React, { useEffect, useState } from "react";
import { useNavigate, useParams } from "react-router-dom";
import logo from "../images/hand-book-logo-illustration-art-background-43965136.webp";
import logo1 from "../images/Book_Image.jpeg";
import TextField from "@mui/material/TextField";
import Button from "@mui/material/Button";
import SendIcon from "@mui/icons-material/Send";
import CloudUploadIcon from "@mui/icons-material/CloudUpload";
import { styled } from "@mui/material/styles";

const BookById = () => {
  const [data, setData] = useState([]);
  const { id } = useParams();
  const [editForm, setEditForm] = useState("none");
  const [displayDetails, setDisplayDetail] = useState("flex");
  const navigate = useNavigate()

  useEffect(() => {
    async function Res() {
      const result = await fetch("http://localhost:5555/books/$");
      const res = await result.json();
      setData(res.data);
    }

    Res();
  });

  const moduleChange = () => {
    setEditForm("block");
    setDisplayDetail("none");
  };

  const reverseModule = () => {
    setEditForm("none");
    setDisplayDetail("flex");
  };

  const DeleteBook = () => {
    if(window.confirm("Are you want it to delete Permanently?")){
    fetch(`http://localhost:3000/book/${id}`, {
      method: "DELETE",
    })
    .then(res => res.json())
    .then(data => console.log(data))
    .catch(error => console.error(error));
    }
    navigate("/allbooks")
  };

  const VisuallyHiddenInput = styled("input")({
    clip: "rect(0 0 0 0)",
    clipPath: "inset(50%)",
    height: 1,
    overflow: "hidden",
    position: "absolute",
    bottom: 0,
    left: 0,
    whiteSpace: "nowrap",
    width: 1,
  });

  return (
    <div>
      <div style={{ display: editForm }} className="updateStudent">
        <div style={{ display: "flex", "margin-bottom": "3%" }}>
          <Button
            onClick={reverseModule}
            style={{
              backgroundColor: "red",
              color: "blue",
              fontWeight: "700",
              padding: "5px",
              "margin-left": "0px",
              "margin-right": "5%",
              border: "1px dotted yellow",
            }}
          >
            Cancel
          </Button>
          <h3>Update Book Details !</h3>
        </div>
        <div className="updateImgInfo">
          <form>
            <TextField
              // value={studentName}
              // onChange={handleStudentName}
              fullWidth
              label="Book Name"
              id="fullWidth"
              margin="normal"
            />
            <TextField
              // value={lastName}
              // onChange={handleLastName}
              fullWidth
              label="Auther Name"
              id="fullWidth"
              margin="normal"
            />
            <TextField
              fullWidth
              // value={address}
              // onChange={handleAddress}
              label="Edition"
              id="fullWidth"
              margin="normal"
            />
            <TextField
              fullWidth
              // value={mail}
              // onChange={handleEmail}
              label="Total Copies"
              id="fullWidth"
              margin="normal"
            />
            <TextField
              fullWidth
              // value={cell}
              // onChange={handleCell}
              type="number"
              label="Available Copies"
              id="fullWidth"
              margin="normal"
            />
            <div className="studentImgBtnDiv">
              <Button
                // onChange={handleFormChange}
                component="label"
                variant="contained"
                color="success"
                startIcon={<CloudUploadIcon />}
              >
                Replace Book Image
                <VisuallyHiddenInput type="file" />
              </Button>
            </div>
            <div className="addbookbtn">
              <Button
                // onClick={NavigateToMainPage}
                fullWidth
                className="btn_In"
                variant="contained"
                size="large"
                endIcon={<SendIcon />}
              >
                Update Book Details
              </Button>
            </div>
          </form>
          <div className="updateStudentImg">
            <img src={logo1} className="updateBookImage" alt="form" />
          </div>
        </div>
      </div>
      {data.map((ele, ind) => {
        if (ele._id === id) {
          return (
            <div
              style={{ display: displayDetails }}
              key={ind}
              className="bookbyid"
            >
              <img src={logo} alt="book Imgage" />
              <div className="bookbyidFlex">
                <div>
                  <h3>Book Name :</h3>
                  <p>{ele.bookName}</p>
                </div>
                <div>
                  <h3>Auther Name :</h3>
                  <p>{ele.autherName}</p>
                </div>
                <div>
                  <h3>Edition :</h3>
                  <p>{ele.edition}</p>
                </div>
                <div>
                  <h3>Total Copies :</h3>
                  <p>{ele.numberCopies}</p>
                </div>
                <div>
                  <h3>Available Copies :</h3>
                  <p>05</p>
                </div>
                <div>
                  <h3>Books Distribution :</h3>
                  <p>Given In Below Table</p>
                </div>
              </div>
              <div className="btnBlock">
                <button className="btngreen" onClick={moduleChange}>
                  Edit
                </button>
                <button className="btnred" onClick={DeleteBook}>
                  Delete
                </button>
              </div>
            </div>
          );
        }
      })}

      <div style={{ display: displayDetails }}>
        <table className="table">
          <tr>
            <th>Sr. No.</th>
            <th>Student Name</th>
            <th>Year</th>
            <th>Branch</th>
            <th>Books Taken</th>
          </tr>
          <tr>
            <td>01</td>
            <td>Kunal Titare</td>
            <td>04</td>
            <td>Mechanical</td>
            <td>01</td>
          </tr>
          <tr>
            <td>02</td>
            <td>Kunal Titare</td>
            <td>04</td>
            <td>Mechanical</td>
            <td>01</td>
          </tr>
        </table>
      </div>
    </div>
  );
};

export default BookById;
