import React, { useState } from "react";
import "../App.css";
import TextField from "@mui/material/TextField";
import Button from "@mui/material/Button";
import logo from "../images/hand-book-logo-illustration-art-background-43965136.webp";
import { useNavigate } from "react-router-dom";
import { Toaster } from "react-hot-toast";
import { useFormik } from "formik";
import { logInValidate } from "../helper/validate";
import { logInValidate1 } from "../helper/validate1";
import { logInValidate2 } from "../helper/validate2";
import toast from "react-hot-toast";

const PasswordReset = () => {

    const forgetPassformik = useFormik({
        initialValues: {
          password: "",
          confirmPassword: ""
        },
        validate: logInValidate2,
        validateOnBlur: false,
        validateOnChange: false,
        onSubmit: async (values) => {
          fetch(`http://localhost:5555/login/forgot-password`, {
            method: "POST",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify({email: values.email}),
          })
          toast("Password Reset Link sent to your Registered Email");
          LogInModule();
        },
      });

  return (
    <div>
      <p className="welcome">WELCOME - TO - LIBRARY</p>
      <form onSubmit={forgetPassformik.handleSubmit}>
        <div className="textfield">
          <TextField
            style={{ display: passwordBlock }}
            {...forgetPassformik.getFieldProps("password")}
            fullWidth
            label="Enter New Password"
            id="fullWidth"
          />
        </div>
        <div className="textfield">
          <TextField
            style={{ display: passwordBlock }}
            {...forgetPassformik.getFieldProps("conPassword")}
            fullWidth
            label="Confirm New Password"
            id="fullWidth"
          />
        </div>
        <Button
          // onClick={NavigateToMainPage}
          type="submit"
          fullWidth
          className="btn_In"
          variant="contained"
          // style={{ marginTop: "2%", display: PassUpdateBtn }}
        >
          Update Password
        </Button>
      </form>
    </div>
  );
};

export default PasswordReset;
