import React, { useState } from "react";
import Box from "@mui/material/Box";
import TextField from "@mui/material/TextField";
import Button from "@mui/material/Button";
import SendIcon from "@mui/icons-material/Send";
import CloudUploadIcon from "@mui/icons-material/CloudUpload";
import { styled } from "@mui/material/styles";
import studentPic from "../images/student_Image.avif";
import studentForm from "../images/student_Form.jpeg";

const NewStudent = () => {
  const [file, setFile] = useState(studentPic);
  const [form, setForm] = useState(studentForm);
  const [studentName, setStudentName] = useState(studentPic);
  const [address, setAddress] = useState(studentPic);
  const [cell, setCell] = useState(studentPic);
  const [branch, setBranch] = useState(studentPic);
  const [mail, setEmail] = useState(studentPic);
  const [year, setYear] = useState(studentPic);
  const [motherName, setFatherName] = useState(studentPic);
  const [parentCell, setParentCell] = useState(studentPic);
  const [lastName, setLastName] = useState(studentPic);
  const [date, setDate] = useState('');

  function handleChange(e) {
    console.log(e.target.files);
    setFile(URL.createObjectURL(e.target.files[0]));
  }

  function handleFormChange(e) {
    console.log(e.target.files);
    setForm(URL.createObjectURL(e.target.files[0]));
  }

  function handleAutherName(e) {
    setAutherName(e.target.value);
  }

  function handleEdition(e) {
    setEdition(e.target.value);
  }

  function handleNumberCopies(e) {
    setNumberCopies(e.target.value);
  }

  function handleDate(e) {
    setDate(e.target.value);
  }

  const NavigateToMainPage = async (e) => {
    e.preventDefault();

    if (
      !studentName ||
      !address ||
      !cell ||
      !branch ||
      !mail ||
      !year ||
      !motherName ||
      !parentCell ||
      !lastName ||
      file === studentPic ||
      form === studentForm
    ) {
      setFile(studentPic);
      setForm(studentForm);
      return alert(
        "Send all required field : Book Name, Auther Name, Edition, Date, Number Of Copies, File"
      );
    }

    const Student = {
      studentName: studentName,
      address: address,
      cell: cell,
      branch: branch,
      email: mail,
      year: year,
      motherName: motherName,
      parentCell: parentCell,
      lastName: lastName,
      studentImg: file,
      studentForm: form,
      bookOrStudent: "Book",
      dateOfJoining: dateOfJoining,
    };

    const requestOptions = {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(Book),
    };

    fetch("http://localhost:5555/books/", requestOptions).then((response) =>
      response.json().then(alert("Book Added In Library Successfully!"))
    );

    setFile(logo);
    setBookName("");
    setAutherName("");
    setEdition("");
    setNumberCopies("");
    setDate("");
  };

  const VisuallyHiddenInput = styled("input")({
    clip: "rect(0 0 0 0)",
    clipPath: "inset(50%)",
    height: 1,
    overflow: "hidden",
    position: "absolute",
    bottom: 0,
    left: 0,
    whiteSpace: "nowrap",
    width: 1,
  });
  return (
    <div className="newStudent">
      <Box
        sx={{
          width: 500,
          maxWidth: "100%",
          margin: 10,
        }}
      >
        <h3>New Students Details !</h3>
        <TextField
          fullWidth
          label="Student Name"
          id="fullWidth"
          margin="normal"
        />
        <TextField fullWidth label="Address" id="fullWidth" margin="normal" />
        <TextField fullWidth label="Email" id="fullWidth" margin="normal" />
        <TextField
          fullWidth
          type="number"
          label="Mobile No."
          id="fullWidth"
          margin="normal"
        />
        <TextField
          fullWidth
          type="date"
          label="Date of Joining"
          id="fullWidth"
          margin="normal"
        />
        <TextField fullWidth label="Branch" id="fullWidth" margin="normal" />
        <TextField
          fullWidth
          type="number"
          label="Year"
          id="fullWidth"
          margin="normal"
        />
        <TextField
          fullWidth
          label="Father Name"
          id="fullWidth"
          margin="normal"
        />
        <TextField
          fullWidth
          label="Mother Name"
          id="fullWidth"
          margin="normal"
        />
        <TextField
          fullWidth
          type="number"
          label="Parents Contact No."
          id="fullWidth"
          margin="normal"
        />
        <div className="studentImgBtnDiv">
          <Button
            onChange={handleChange}
            component="label"
            variant="contained"
            color="success"
            startIcon={<CloudUploadIcon />}
          >
            Upload Student Image
            <VisuallyHiddenInput type="file" />
          </Button>

          <Button
            onChange={handleFormChange}
            component="label"
            variant="contained"
            color="success"
            startIcon={<CloudUploadIcon />}
          >
            Upload Form
            <VisuallyHiddenInput type="file" />
          </Button>
        </div>
        <div className="addbookbtn">
          <Button
            onClick={NavigateToMainPage}
            fullWidth
            className="btn_In"
            variant="contained"
            size="large"
            endIcon={<SendIcon />}
          >
            Add Student
          </Button>
        </div>
      </Box>
      <div className="studentImg">
        <img src={file} className="studentImage" alt="student" />
        <img src={form} className="formImage" alt="form" />
      </div>
    </div>
  );
};

export default NewStudent;
