import React, {useState} from 'react';
import Box from "@mui/material/Box";
import TextField from "@mui/material/TextField";
import { useNavigate } from "react-router-dom";
import Button from "@mui/material/Button";
import SendIcon from "@mui/icons-material/Send";
import CloudUploadIcon from "@mui/icons-material/CloudUpload";
import { styled } from "@mui/material/styles"

const NewStudent = () => {

  const [file, setFile] = useState();
  const [form, setForm] = useState();

  function handleChange(e) {
    console.log(e.target.files);
    setFile(URL.createObjectURL(e.target.files[0]));
  }

  function handleFormChange(e) {
    console.log(e.target.files);
    setForm(URL.createObjectURL(e.target.files[0]));
  }

  const navigate = useNavigate();

  const NavigateToMainPage = () => {
    navigate("/allstudents");
  };

  const VisuallyHiddenInput = styled("input")({
    clip: "rect(0 0 0 0)",
    clipPath: "inset(50%)",
    height: 1,
    overflow: "hidden",
    position: "absolute",
    bottom: 0,
    left: 0,
    whiteSpace: "nowrap",
    width: 1,
  });
  return (
    <div>
      <Box
        sx={{
          width: 500,
          maxWidth: "100%",
          margin: 10,
        }}
      >
        <h3>New Students Details !</h3>
        <TextField fullWidth label="Student Name" id="fullWidth" margin="normal" />
        <TextField
          fullWidth
          label="Address"
          id="fullWidth"
          margin="normal"
        />
        <TextField fullWidth label="Email" id="fullWidth" margin="normal" />
        <TextField
          fullWidth
          type="number"
          label="Mobile No."
          id="fullWidth"
          margin="normal"
        />
        <TextField
          fullWidth
          type="date"
          label="Date of Joining"
          id="fullWidth"
          margin="normal"
        />
        <div className=''>
        <Button
          onChange={handleChange}
          component="label"
          variant="contained"
          color="success"
          startIcon={<CloudUploadIcon />}
        >
          Upload Student Image
          <VisuallyHiddenInput type="file" />
        </Button>

        <Button
          onChange={handleFormChange}
          component="label"
          variant="contained"
          color="success"
          startIcon={<CloudUploadIcon />}
        >
          Upload Form
          <VisuallyHiddenInput type="file" />
        </Button>
        </div>
        <div className="addbookbtn">
          <Button
            onClick={NavigateToMainPage}
            fullWidth
            className="btn_In"
            variant="contained"
            size="large"
            endIcon={<SendIcon />}
          >
            Add Student
          </Button>
        </div>
      </Box>
      <img src={file} className="bookImage" />
      <img src={form} className="bookImage" />
    </div>
  )
}

export default NewStudent
