import React, { useEffect, useState } from "react";
import { useNavigate, useParams } from "react-router-dom";
import logo from "../images/hand-book-logo-illustration-art-background-43965136.webp";
import logo1 from "../images/Book_Image.jpeg";
import TextField from "@mui/material/TextField";
import Button from "@mui/material/Button";
import SendIcon from "@mui/icons-material/Send";
import CloudUploadIcon from "@mui/icons-material/CloudUpload";
import { styled } from "@mui/material/styles";

const BookById = () => {
  const [data, setData] = useState([]);
  const { id } = useParams();
  const [editForm, setEditForm] = useState("none");
  const [displayDetails, setDisplayDetail] = useState("flex");
  const navigate = useNavigate();

  const [file, setFile] = useState(logo);
  const [bookName, setBookName] = useState("");
  const [autherName, setAutherName] = useState("");
  const [edition, setEdition] = useState("");
  const [numberCopies, setNumberCopies] = useState("");
  const [date, setDate] = useState("");

  useEffect(() => {
    async function Res() {
      const result = await fetch(`http://localhost:5555/books/${id}`);
      const res = await result.json();
      setData(res);
    }

    Res();
  }, [id]);

  const moduleChange = () => {
    setEditForm("block");
    setDisplayDetail("none");
    setBookName(data.bookName);
    setAutherName(data.autherName);
    setEdition(data.edition);
    setNumberCopies(data.numberCopies);
    setDate(data.date);
  };

  const reverseModule = () => {
    setEditForm("none");
    setDisplayDetail("flex");
  };

  async function DeleteBook () {
    console.log(id)
    const requestOptions = {
      method: "DELETE",
      headers: { "Content-Type": "application/json" },
    };
    if (window.confirm("Are you want it to delete Permanently?")) {
      await fetch(`http://localhost:5555/books/${id}`,  {method:"PATCH", headers: { "Content-Type": "application/json" }, body: JSON.stringify(Book)})
        .then((res) => res.json())
        .then((data) => console.log(data))
        .catch((error) => console.error(error));
    }
    navigate("/allbooks");
  };

  const VisuallyHiddenInput = styled("input")({
    clip: "rect(0 0 0 0)",
    clipPath: "inset(50%)",
    height: 1,
    overflow: "hidden",
    position: "absolute",
    bottom: 0,
    left: 0,
    whiteSpace: "nowrap",
    width: 1,
  });

  const handleBookName = (e) => {
    setBookName(e.target.value)
  }
  const handleAutherName = (e) => {
    setAutherName(e.target.value)
  }
  const handleEdition = (e) => {
    setEdition(e.target.value)
  }
  const handleNumberCopies = (e) => {
    setNumberCopies(e.target.value)
  }
  const handleBookUpdate = (e) => {
    e.preventDefault();

    if (
      !bookName ||
      !autherName ||
      !edition ||
      !date ||
      !numberCopies
    ) {
      setFile(logo);
      return alert(
        "Send all required field : Book Name, Auther Name, Edition, Date, Number Of Copies, File"
      );
    }

    const Book = {
      bookName: bookName,
      autherName: autherName,
      edition: edition,
      date: date,
      numberCopies: numberCopies,
      file: file,
    };

    console.log(Book)

    fetch(`http://localhost:5555/books/${id}`, {method:"PATCH", headers: { "Content-Type": "application/json" }, body: JSON.stringify(Book)}).then((response) =>
      response.json().then(alert("Book Updated In Library Successfully!"))
    );

    setEditForm("none");
    setDisplayDetail("flex");

  }

  return (
    <div>
      <div style={{ display: editForm }} className="updateStudent">
        <div style={{ display: "flex", "margin-bottom": "3%" }}>
          <Button
            onClick={reverseModule}
            style={{
              backgroundColor: "red",
              color: "blue",
              fontWeight: "700",
              padding: "5px",
              "margin-left": "0px",
              "margin-right": "5%",
              border: "1px dotted yellow",
            }}
          >
            Cancel
          </Button>
          <h3>Update Book Details !</h3>
        </div>
        <div className="updateImgInfo">
          <form>
            <TextField
              value={bookName}
              onChange={handleBookName}
              fullWidth
              label="Book Name"
              id="fullWidth"
              margin="normal"
            />
            <TextField
              value={autherName}
              onChange={handleAutherName}
              fullWidth
              label="Auther Name"
              id="fullWidth"
              margin="normal"
            />
            <TextField
              fullWidth
              value={edition}
              onChange={handleEdition}
              label="Edition"
              id="fullWidth"
              margin="normal"
            />
            <TextField
              fullWidth
              value={numberCopies}
              onChange={handleNumberCopies}
              label="Total Copies"
              id="fullWidth"
              margin="normal"
            />
            <div className="studentImgBtnDiv">
              <Button
                // onChange={handleImage}
                component="label"
                variant="contained"
                color="success"
                startIcon={<CloudUploadIcon />}
              >
                Replace Book Image
                <VisuallyHiddenInput type="file" />
              </Button>
            </div>
            <div className="addbookbtn">
              <Button
                onClick={handleBookUpdate}
                type="submit"
                fullWidth
                className="btn_In"
                variant="contained"
                size="large"
                endIcon={<SendIcon />}
              >
                Update Book Details
              </Button>
            </div>
          </form>
          <div className="updateStudentImg">
            <img src={logo1} className="updateBookImage" alt="form" />
          </div>
        </div>
      </div>
      <div style={{ display: displayDetails }} className="bookbyid">
        <img src={logo} alt="book Imgage" />
        <div className="bookbyidFlex">
          <div>
            <h3>Book Name :</h3>
            <p>{data.bookName}</p>
          </div>
          <div>
            <h3>Auther Name :</h3>
            <p>{data.autherName}</p>
          </div>
          <div>
