import React from "react";
import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";

const AllBooks = () => {
  const [data, setData] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    async function Res() {
      const result = await fetch("http://localhost:5555/books");
      const res = await result.json();
      setData(res.data);
    }

    Res();
  });

  const SingleBook = (id) => {
    navigate(`/book/${id}`)
  }

  return (
    <div className="allbooks">
      <table className="table">
        <tr>
          <th>Sr. No.</th>
          <th>Book Name</th>
          <th>Auther Name</th>
          <th>Total Copies</th>
          <th>Available Copies</th>
          <th> More Info / Edit / Delete</th>
        </tr>
        {data.map((ele, ind) => {
          return (
            <tr key={ind}>
              <td>{ind+1}</td>
              <td>{ele.bookName}</td>
              <td>{ele.autherName}</td>
              <td>{ele.numberCopies}</td>
              <td>{ele.numberCopies}</td>
              <td className="tdbtn">
                <button className="btnblue" onClick={(e) => SingleBook(ele._id)}>More Info./ Edit / Delete</button>
              </td>
            </tr>
          );
        })}
      </table>
    </div>
  );
};

export default AllBooks;
