import React from "react";
import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";

const AllStudents = () => {
  const [data, setData] = useState([]);
  const [count, setCount] = useState([]);
  const navigate = useNavigate('');

  useEffect(() => {
    async function Res() {
      const token = localStorage.getItem('token');
      const result = await fetch("http://localhost:5555/students", { headers: {"Authorization" : `${token}`} });
      const res = await result.json();
      setCount(res.count);
      setData(res.data);
    }

    Res();
  },[]);

  const SingleStudent = (id) => {
    navigate(`/student/${id}`);
  };

  return (
    <div className="allstudents">
      <table className="table">
      <tr>
          <th className="totalBooks" colSpan={6}>Total Students In Library: <span>{count}</span></th>
        </tr>
        <tr>
          <th>Sr. No.</th>
          <th>Student Name</th>
          <th>Year</th>
          <th>Branch</th>
          <th>Books Taken</th>
          <th> More Info / Edit / Delete</th>
        </tr>
        {data.map((ele, ind) => {
          return (
            <tr key={ind}>
              <td>{ind + 1}</td>
              <td>{ele.studentName + " " + ele.lastName}</td>
              <td>{ele.year}</td>
              <td>{ele.branch}</td>
              <td>05</td>
              <td className="tdbtn">
                <button className="btnblue" onClick={(e) => SingleStudent(ele._id)}>More Info. / Edit / Delete</button>
              </td>
            </tr>
          );
        })}
      </table>
    </div>
  );
};

export default AllStudents;
