import React, { useState } from "react";
import Box from "@mui/material/Box";
import TextField from "@mui/material/TextField";
import Button from "@mui/material/Button";
import SendIcon from "@mui/icons-material/Send";
import CloudUploadIcon from "@mui/icons-material/CloudUpload";
import { styled } from "@mui/material/styles";
import logo from "../images/Book_Image.jpeg";
import { useNavigate } from "react-router-dom";


const AddBook = () => {
  const [file, setFile] = useState(logo);
  const [bookName, setBookName] = useState("");
  const [autherName, setAutherName] = useState("");
  const [edition, setEdition] = useState("");
  const [date, setDate] = useState("");
  const [numberCopies, setNumberCopies] = useState("");

  function handleChange(e) {
    var reader = new FileReader();
    reader.readAsDataURL(e.target.files[0]);
    reader.onload = () =>{
      setFile(reader.result);
    }
    reader.onerror = (error) => {
      console.log(error);
    }
  }

  function handleBookName(e) {
    setBookName(e.target.value);
  }

  function handleAutherName(e) {
    setAutherName(e.target.value);
  }

  function handleEdition(e) {
    setEdition(e.target.value);
  }

  function handleNumberCopies(e) {
    setNumberCopies(e.target.value);
  }

  function handleDate(e) {
    setDate(e.target.value);
  }

  const navigate = useNavigate;

  const NavigateToMainPage = async (e) => {
    e.preventDefault();

    if (
      !bookName ||
      !autherName ||
      !edition ||
      !date ||
      !numberCopies ||
      file === logo
    ) {
      setFile(logo);
      return alert(
        "Send all required field : Book Name, Auther Name, Edition, Date, Number Of Copies, File"
      );
    }

    const Book = {
      bookName: bookName,
      autherName: autherName,
      edition: edition,
      date: date,
      numberCopies: numberCopies,
      file: file,
      bookOrStudent: 'Book',
    };

    const requestOptions = {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(Book),
    };

    fetch("http://localhost:5555/books/", requestOptions).then((response) =>
      response.json().then(alert("Book Added In Library Successfully!"))
    );

    setFile(logo);
    setBookName("");
    setAutherName("");
    setEdition("");
    setNumberCopies("");
    setDate("");
    navigate("/allbooks");
  };

  const VisuallyHiddenInput = styled("input")({
    clip: "rect(0 0 0 0)",
    clipPath: "inset(50%)",
    height: 1,
    overflow: "hidden",
    position: "absolute",
    bottom: 0,
    left: 0,
    whiteSpace: "nowrap",
    width: 1,
  });

  return (
    <form className="addbook" encType="multipart/form-data">
      <Box
        sx={{
          width: 500,
          maxWidth: "100%",
          margin: 10,
          color
        }}
      >
        <h3>Fill New Books Details Here!</h3>
        <TextField
          value={bookName}
          onChange={handleBookName}
          fullWidth
          label="Book Name"
          id="fullWidth"
          margin="normal"
        />
        <TextField
          value={autherName}
          onChange={handleAutherName}
          fullWidth
          label="Auther Name"
          id="fullWidth"
          margin="normal"
        />
        <TextField
          value={edition}
          onChange={handleEdition}
          fullWidth
          type="number"
          label="Edition"
          id="fullWidth"
          margin="normal"
        />
        <TextField
          value={numberCopies}
          onChange={handleNumberCopies}
          fullWidth
          type="number"
          label="Number of Copies"
          id="fullWidth"
          margin="normal"
        />
        <TextField
          value={date}
          onChange={handleDate}
          fullWidth
          type="date"
          label="Available In Library Date"
          id="fullWidth"
          margin="normal"
        />
        <Button
          onChange={handleChange}
          component="label"
          variant="contained"
          color="success"
          startIcon={<CloudUploadIcon />}
        >
          Upload Image
          <VisuallyHiddenInput type="file" />
        </Button>
        <div className="addbookbtn">
          <Button
            onClick={NavigateToMainPage}
            fullWidth
            className="btn_In"
            variant="contained"
            size="large"
            endIcon={<SendIcon />}
          >
            Add Book
          </Button>
        </div>
      </Box>
      <img src={file} className="bookImage" alt="book" />
    </form>
  );
};

export default AddBook;
