import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import logo from "../images/hand-book-logo-illustration-art-background-43965136.webp";

const BookById = () => {
  const [data, setData] = useState([]);
  const { id } = useParams();

  useEffect(() => {
    async function Res() {
      const result = await fetch("http://localhost:5555/students");
      const res = await result.json();
      setData(res.data);
    }

    Res();
  });
  return (
    <div>
      {data.map((ele, ind) => {
        if (ele._id === id) {
          return (
            <div key={ind} className="bookbyid">
              <img src={logo} alt="book Imgage" />
              <div className="bookbyidFlex">
                <div>
                  <h3>Book Name :</h3>
                  <p>{ele.bookName}</p>
                </div>
                <div>
                  <h3>Auther Name :</h3>
                  <p>{
                  }</p>
                </div>
                <div>
                  <h3>Edition :</h3>
                  <p>04</p>
                </div>
                <div>
                  <h3>Total Copies :</h3>
                  <p>15</p>
                </div>
                <div>
                  <h3>Available Copies :</h3>
                  <p>05</p>
                </div>
                <div>
                  <h3>Books Distribution :</h3>
                  <p>Given In Below Table</p>
                </div>
              </div>
            </div>
          );
        }
      })}

      <div>
        <table className="table">
          <tr>
            <th>Sr. No.</th>
            <th>Student Name</th>
            <th>Year</th>
            <th>Branch</th>
            <th>Books Taken</th>
          </tr>
          <tr>
            <td>01</td>
            <td>Kunal Titare</td>
            <td>04</td>
            <td>Mechanical</td>
            <td>01</td>
          </tr>
          <tr>
            <td>02</td>
            <td>Kunal Titare</td>
            <td>04</td>
            <td>Mechanical</td>
            <td>01</td>
          </tr>
        </table>
      </div>
    </div>
  );
};

export default BookById;
