import React, { useEffect, useState } from "react";
import { useNavigate, useParams } from "react-router-dom";
import logo from "../images/hand-book-logo-illustration-art-background-43965136.webp";
import TextField from "@mui/material/TextField";
import logo1 from "../images/Book_Image.jpeg";
import Button from "@mui/material/Button";
import SendIcon from "@mui/icons-material/Send";
import CloudUploadIcon from "@mui/icons-material/CloudUpload";
import { styled } from "@mui/material/styles";

const BookById = () => {
  let index = 0;
  let [bookCopies, setBookCopies] = useState(0);
  const [data, setData] = useState([]);
  const [updateData, setUpdateData] = useState([]);
  const [studentId, setStudentId] = useState();
  const { id } = useParams();
  const [editForm, setEditForm] = useState("none");
  const [addStudent, setAddStudent] = useState("none");
  const [displayDetails, setDisplayDetail] = useState("flex");
  const [studentList, setstudentList] = useState([]);
  const [studenName, setStudenName] = useState("");
  const [allStudents, setAllStudents] = useState([]);
  const navigate = useNavigate();
  const token = localStorage.getItem("token");

  const [newData, setNewData] = useState({
    file: logo,
    bookName: " ",
    autherName: " ",
    edition: " ",
    numberCopies: " ",
    date: " ",
  });

  const [studentData, setStudentData] = useState();

  useEffect(() => {
    async function Res() {
      const result = await fetch(`http://localhost:5555/books/${id}`, {
        headers: {
          authorization: `${token}`,
          Accept: "application/json",
          "Content-Type": "application/json",
        },
      });
      const res = await result.json();
      if (res.error == "Authentication Failed") {
        navigate("/");
      }
      setData(res);
    }

    Res();
  }, [id, updateData]);

  useEffect(() => {
    async function ResStudents() {
      const token = localStorage.getItem("token");
      const result = await fetch("http://localhost:5555/students", {
        headers: { Authorization: `${token}` },
      });
      const res = await result.json();
      if (res.error == "Authentication Failed") {
        navigate("/");
      }
      setAllStudents(res.data);
    }
    ResStudents();
  }, []);

  useEffect(() => {
    let result = parseInt(data.numberCopies) - parseInt(index);
    setBookCopies(parseInt(result));
    console.log(data.numberCopies, index, result, bookCopies)
  });

  const moduleChange = () => {
    setEditForm("block");
    setAddStudent("none");
    setDisplayDetail("none");
    setNewData({
      bookName: data.bookName,
      autherName: data.autherName,
      edition: data.edition,
      numberCopies: data.numberCopies,
      date: data.date,
    });
  };

  const moduleAddStudent = () => {
    setAddStudent("block");
    setEditForm("none");
    setDisplayDetail("none");
    setNewData({
      bookName: data.bookName,
      autherName: data.autherName,
      edition: data.edition,
      numberCopies: data.numberCopies,
      date: data.date,
    });
  };

  const reverseModule = () => {
    setEditForm("none");
    setAddStudent("none");
    setDisplayDetail("flex");
  };

  async function DeleteBook() {
    if (window.confirm("Are you want it to delete Permanently?")) {
      await fetch(`http://localhost:5555/books/${id}`, {
        method: "DELETE",
        headers: { authorization: `${token}` },
      })
        .then((res) => {
          res.json();
          if (res.error == "Authentication Failed") {
            navigate("/");
          }
        })
        .then((data) => console.log(data))
        .catch((error) => console.error(error));
    }
    navigate("/allbooks");
  }

  const VisuallyHiddenInput = styled("input")({
    clip: "rect(0 0 0 0)",
    clipPath: "inset(50%)",
    height: 1,
    overflow: "hidden",
    position: "absolute",
    bottom: 0,
    left: 0,
    whiteSpace: "nowrap",
    width: 1,
  });

  const handleImage = (e) => {
    var reader = new FileReader();
    reader.readAsDataURL(e.target.files[0]);
    reader.onload = () => {
      setNewData({ ...data, file: reader.result });
    };
    reader.onerror = (error) => {
      console.log(error);
    };
  };

  let name, value;
  const handleInputs = (e) => {
    name = e.target.name;
    if (name !== "file") {
      value = e.target.value;
    }
    setNewData({ ...newData, [name]: value });
  };

  const handleStudentInputs = async (e) => {
    value = e.target.value;
    setStudenName(value);
    const result = await fetch("http://localhost:5555/students/studentList", {
      method: "POST",
      headers: { authorization: `${token}` },
      body: JSON.stringify({
        value: studenName,
      }),
    });
    const listofstudent = await result.json();
    setstudentList(listofstudent);
  };

  const handleBookUpdate = (e) => {
    e.preventDefault();

    if (
      !newData.bookName ||
      !newData.autherName ||
      !newData.edition ||
      !newData.date ||
      !newData.numberCopies ||
      !newData.file
    ) {
      setNewData({ ...newData, file: logo });
      return alert(
        "Send all required field : Book Name, Auther Name, Edition, Date, Number Of Copies, File"
      );
    }

    fetch(`http://localhost:5555/books/${id}`, {
      method: "PATCH",
      headers: {
        "Content-Type": "application/json",
        authorization: `${token}`,
      },
      body: JSON.stringify(newData),
    }).then((response) =>
      response.json().then(alert("Book Updated In Library Successfully!"))
    );

    setEditForm("none");
    setDisplayDetail("flex");
    setUpdateData(newData);
  };

  const handleClickOnStudent = (ele) => {
    console.log(ele.books);
    setStudenName(ele.studentName + " " + ele.lastName);
    setStudentId(ele._id);
    setStudentData({
      studentImg: ele.studentImg,
      studentForm: ele.studentForm,
      studentName: ele.studentName,
      lastName: ele.lastName,
      address: ele.address,
      cell: ele.cell,
      branch: ele.branch,
      email: ele.email,
      year: ele.year,
      motherName: ele.motherName,
      fatherName: ele.fatherName,
      parentCell: ele.parentCell,
      dateOfJoining: ele.dateOfJoining,
      books: ele.books
        ? [
            ...ele.books,
            {
              BookName: newData.bookName,
              AutherName: newData.autherName,
              edition: newData.edition,
            },
          ]
        : [
            {
              BookName: newData.bookName,
              AutherName: newData.autherName,
              edition: newData.edition,
            },
          ],
    });
  };

  const handleStudentUpdate = (e) => {
    e.preventDefault();
    if(data.numberCopies <= bookCopies){
      alert ("")
    } else{
      const requestOptions = {
        method: "PATCH",
        headers: {
          "Content-Type": "application/json",
          authorization: `${token}`,
        },
        body: JSON.stringify(studentData),
      };

      fetch(`http://localhost:5555/students/${studentId}`, requestOptions).then(
        (response) =>
          response
            .json()
            .then(alert("Student Table Updated In Library Successfully!"))
      );
    }
    reverseModule();
  };

  return (
    <div>
      <div style={{ display: editForm }} className="updateStudent">
        <div style={{ display: "flex", "margin-bottom": "3%" }}>
          <Button
            onClick={reverseModule}
            style={{
              backgroundColor: "red",
              color: "blue",
              fontWeight: "700",
              padding: "5px",
              "margin-left": "0px",
              "margin-right": "5%",
              border: "1px dotted yellow",
            }}
          >
            Cancel
          </Button>
          <h3>Update Book Details !</h3>
        </div>
        <div className="updateImgInfo">
          <form>
            <TextField
              name="bookName"
              value={newData.bookName}
              onChange={handleInputs}
              fullWidth
              label="Book Name"
              id="fullWidth"
              margin="normal"
            />
            <TextField
              name="autherName"
              value={newData.autherName}
              onChange={handleInputs}
              fullWidth
              label="Auther Name"
              id="fullWidth"
              margin="normal"
            />
            <TextField
              fullWidth
              name="edition"
              value={newData.edition}
              onChange={handleInputs}
              label="Edition"
              id="fullWidth"
              margin="normal"
            />
            <TextField
              fullWidth
              name="numberCopies"
              value={newData.numberCopies}
              onChange={handleInputs}
              label="Total Copies"
              id="fullWidth"
              margin="normal"
            />
            <div className="studentImgBtnDiv">
              <Button
                onChange={handleImage}
                component="label"
                variant="contained"
                color="success"
                startIcon={<CloudUploadIcon />}
              >
                Replace Book Image
                <VisuallyHiddenInput type="file" />
              </Button>
            </div>
            <div className="addbookbtn">
              <Button
                onClick={handleBookUpdate}
                type="submit"
                fullWidth
                className="btn_In"
                variant="contained"
                size="large"
                endIcon={<SendIcon />}
              >
                Update Book Details
              </Button>
            </div>
          </form>
          <div className="updateStudentImg">
            <img
              src={newData.file ? newData.file : logo1}
              className="updateBookImage"
              alt="form"
            />
          </div>
        </div>
      </div>
      <div style={{ display: addStudent }} className="updateStudent">
        <div style={{ display: "flex", "margin-bottom": "3%" }}>
          <Button
            onClick={reverseModule}
            style={{
              backgroundColor: "red",
              color: "blue",
              fontWeight: "700",
              padding: "5px",
              "margin-left": "0px",
              "margin-right": "5%",
              border: "1px dotted yellow",
            }}
          >
            Cancel
          </Button>
          <h3>Add New Student who taking this Book !</h3>
        </div>
        <div className="updateImgInfo" style={{ display: "flex" }}>
          <form style={{ width: "50%" }}>
            <div style={{ display: "flex" }}>
              <p style={{ fontSize: "large", color: "white", width: "100%" }}>
                <span style={{ fontWeight: "bold" }}>Book:- </span>
                {newData.bookName}
              </p>
              <p style={{ fontSize: "large", color: "white", width: "100%" }}>
                <span style={{ fontWeight: "bold" }}>Auther:- </span>
                {newData.autherName}
              </p>
            </div>
            <TextField
              name="StudentName"
              value={studenName}
              onChange={handleStudentInputs}
              fullWidth
              label="Student Name"
              id="fullWidth"
              margin="normal"
              style={{ marginTop: "10%" }}
            />
            <div className="addbookbtn">
              <Button
                onClick={handleStudentUpdate}
                type="submit"
                fullWidth
                className="btn_In"
                variant="contained"
                size="large"
                endIcon={<SendIcon />}
              >
                Update Student Table
              </Button>
            </div>
          </form>
          <div style={{ width: "45%", margin: "2%" }}>
            {studentList.map((ele, ind) => {
              if (
                ele.lastName.includes(studenName) ||
                ele.studentName.includes(studenName) ||
                ele._id.includes(studenName)
              ) {
                return (
                  <div
                    key={ind}
                    onClick={(e) => handleClickOnStudent(ele)}
                    style={{ color: "black", display: "flex", marginTop: "5%" }}
                  >
                    <p
                      style={{
                        color: "blue",
                        fontWeight: "bolder",
                        marginLeft: "2%",
                        marginRight: "3%",
                      }}
                    >
                      {ind + 1})
                    </p>
                    <div>
                      <p>
                        Id:-
                        <span style={{ fontWeight: "bolder" }}>{ele._id}</span>
                      </p>
                      <p>
                        Name:-
                        <span style={{ fontWeight: "bolder" }}>
                          {ele.studentName + " " + ele.lastName}
                        </span>
                      </p>
                    </div>
                  </div>
                );
              }
            })}
          </div>
        </div>
      </div>
      <div style={{ display: displayDetails }} className="bookbyid">
        <img src={data.file ? data.file : logo} alt="book Imgage" />
        <div className="bookbyidFlex">
          <div>
            <h3>Book Name :</h3>
            <p>{data.bookName}</p>
          </div>
          <div>
            <h3>Auther Name :</h3>
            <p>{data.autherName}</p>
          </div>
          <div>
            <h3>Edition :</h3>
            <p>{data.edition}</p>
          </div>
          <div>
            <h3>Total Copies :</h3>
            <p>{data.numberCopies}</p>
          </div>
          <div>
            <h3>Available Copies :</h3>
            <p>{bookCopies}</p>
          </div>
          <div>
            <h3>Books Distribution :</h3>
            <p>Given In Below Table</p>
          </div>
        </div>
        <div className="btnBlock">
          <button className="btnYellow" onClick={moduleAddStudent}>
            Add Student
          </button>
          <button className="btngreen" onClick={moduleChange}>
            Edit
          </button>
          <button className="btnred" onClick={DeleteBook}>
            Delete
          </button>
        </div>
      </div>

      <div style={{ display: displayDetails, overflow: "auto" }}>
        <table className="table">
          <tr>
            <th>Sr. No.</th>
            <th>Student Name</th>
            <th>Year</th>
            <th>Branch</th>
            <th>Books Taken</th>
          </tr>
            {allStudents.map((ele, ind) => {
              if (ele.books) {
                return ele.books.map((singleBook, ind) => {
                  if (singleBook.BookName === data.bookName) {
                    index++;
                    return (
                      <tr key={ind}>
                        <td>{index}</td>
                        <td>
                          { ele.studentName +
                            " " +
                            ele.lastName}
                        </td>
                        <td>{ele.year}</td>
                        <td>{ele.branch}</td>
                        <td>01</td>
                      </tr>
                    );
                  }
                });
              }
            })}
        </table>
      </div>
    </div>
  );
};

export default BookById;
