import React, { useState } from "react";
import "../App.css";
import TextField from "@mui/material/TextField";
import Button from "@mui/material/Button";
import logo from "../images/hand-book-logo-illustration-art-background-43965136.webp";
import { useNavigate } from "react-router-dom";
import { Toaster } from "react-hot-toast";
import { useFormik } from "formik";
import { logInValidate } from "../helper/validate";
import { logInValidate1 } from "../helper/validate1";
import { logInValidate3 } from "../helper/validate3";
import { logInValidate4 } from "../helper/validate4";
import toast from "react-hot-toast";

const Home = () => {
  const [logInBlock, setLoginBlock] = useState("block");
  const [getOtpBlock, setGetOtpBlock] = useState("none");
  const [registerBlock, setRegisterBlock] = useState("none");
  const [forgetPassBlock, setForgetPassBlock] = useState("none");
  const [newName, setNewName] = useState("");
  const [newEmail, setNewEmail] = useState("");
  const [newMobile, setNewMobile] = useState("");
  const [newPassword, setNewPassword] = useState("");

  const navigate = useNavigate();

  const GetOTPModule = () => {
    setLoginBlock("none");
    setGetOtpBlock("block");
    setForgetPassBlock("none");
  };

  const RegisterModule = () => {
    setLoginBlock("none");
    setGetOtpBlock("none");
    setRegisterBlock("block");
    setForgetPassBlock("none");
  };

  const LogInModule = () => {
    setLoginBlock("block");
    setGetOtpBlock("none");
    setRegisterBlock("none");
    setForgetPassBlock("none");
  };

  const handleForgetPassword = () => {
    setForgetPassBlock("block");
    setLoginBlock("none");
    setGetOtpBlock("none");
    setRegisterBlock("none");
  };

  async function GuestHandling() {
    const accesstoken = await fetch("http://localhost:5555/login/login", {
      method: "POST",
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        email: "titarekunal@gmail.com",
        password: "Kunal@1309",
      }),
    });

    const token = await accesstoken.json();
    localStorage.setItem("token", `${"bearer" + " " + token}`);
    navigate("/allbooks");
  }

  async function OtpResend() {
    const mailOtp = await fetch(
      "http://localhost:5555/login/generateOTP/titarekunal@gmail.com",
      { method: "GET", headers: { "Content-Type": "application/json" } }
    );
    const otp = await mailOtp.json();
    let realOtp = otp.otp;
    fetch("http://localhost:5555/login/registerMail", {
      method: "POST",
      // mode: "no-cors",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ otp: realOtp }),
    });
  }

  const formik = useFormik({
    initialValues: {
      email: "",
      password: "",
    },
    validate: logInValidate,
    validateOnBlur: false,
    validateOnChange: false,
    onSubmit: async (values) => {
      const accesstoken = await fetch("http://localhost:5555/login/login", {
        method: "POST",
        headers: {
          Accept: "application/json",
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          email: values.email,
          password: values.password,
        }),
      })
      const token = await accesstoken.json();
      if (token.error == "Password is wrong") {
            return alert("Password is wrong");
          }
      formik.setValues({
        email: "",
        password: "",
      })
      localStorage.setItem("token", `${"bearer" + " " + token}`);
      navigate("/allbooks");
    },
  });

  const otpformik = useFormik({
    initialValues: {
      name: "",
      email: "",
      mobile: "",
      password: "",
      conpassword: "",
    },
    validate: logInValidate1,
    validateOnBlur: false,
    validateOnChange: false,
    onSubmit: async (values) => {
      const mailOtp = await fetch(
        "http://localhost:5555/login/generateOTP/titarekunal@gmail.com",
        { method: "GET", headers: { "Content-Type": "application/json" } }
      );
      const otp = await mailOtp.json();
      let realOtp = otp.otp;
      fetch("http://localhost:5555/login/registerMail", {
        method: "POST",
        // mode: "no-cors",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          otp: realOtp,
          name: values.name,
          email: values.email,
          mobile: values.mobile,
        }),
      });
      setNewName(values.name);
      setNewEmail(values.email);
      setNewMobile(values.mobile);
      setNewPassword(values.password);
      otpformik.setValues({
        name: "",
        email: "",
        mobile: "",
        password: "",
        conpassword: "",
      });
      RegisterModule();
    },
  });

  const registerformik = useFormik({
    initialValues: {
      otp: "",
    },
    validate: logInValidate4,
    validateOnBlur: false,
    validateOnChange: false,
    onSubmit: async (values) => {
      fetch(`http://localhost:5555/login/verifyOTP/${values.otp}`)
        .then((response) => {
          response.json();
          if (response.error == "Invalid OTP") {
            return alert("Invalid OTP!");
          }
          fetch("http://localhost:5555/login/register", {
            method: "POST",
            headers: {
              Accept: "application/json",
              "Content-Type": "application/json",
            },
            body: JSON.stringify({
              name: newName,
              email: newEmail,
              mobile: newMobile,
              password: newPassword,
            }),
          });
        })
        .then(
          alert("Registration completed Successfully. Now you can LogIn !")
        );
      registerformik.setValues({ otp: "" });
      setNewName("");
      setNewEmail("");
      setNewMobile("");
      setNewPassword("");
      LogInModule();
    },
  });

  const forgetPassformik = useFormik({
    initialValues: {
      email: "",
    },
    validate: logInValidate3,
    validateOnBlur: false,
    validateOnChange: false,
    onSubmit: async (values) => {
      fetch(`http://localhost:5555/login/forgot-password`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ email: values.email }),
      });
      toast("Password Reset Link sent to your Registered Email");
      LogInModule();
    },
  });

  return (
    <div className="home">
      <img className="homeLogo" src={logo} alt="library logo" />

      <Toaster></Toaster>

      <div className="homeFirstDiv" style={{ display: logInBlock }}>
        <p className="welcome">WELCOME - TO - LIBRARY</p>
        <p className="slok">
          I measure the progress of a community by the degree of progress which
          women have achieved... Dr. B. R. Ambedkar
        </p>
        <form onSubmit={formik.handleSubmit}>
          <div className="homeFirstDiv_childFirstDiv">
            <div className="textfield">
              <TextField
                {...formik.getFieldProps("email")}
                fullWidth
                label="Email"
                id="fullWidth"
              />
            </div>
            <div className="textfield">
              <TextField
                {...formik.getFieldProps("password")}
                fullWidth
                label="Password"
                type="password"
                id="fullWidth"
              />
            </div>
          </div>
          <Button
            type="submit"
            fullWidth
            className="btn_In"
            variant="contained"
          >
            Sign-In
          </Button>
        </form>
        <p className="or" onClick={handleForgetPassword}>
          Forgate Password ?
        </p>
        <p className="or" onClick={GetOTPModule}>
          New Here ?
        </p>
        <p className="or" onClick={GuestHandling}>
          Guest Log-In ?
        </p>
      </div>

      <div className="homeSecondDiv" style={{ display: getOtpBlock }}>
        <p className="welcome">WELCOME - TO - LIBRARY</p>
        <form onSubmit={otpformik.handleSubmit}>
          <div className="homeFirstDiv_childFirstDiv">
            <div className="textfield">
              <TextField
                {...otpformik.getFieldProps("name")}
                fullWidth
                label="Name"
                id="fullWidth"
              />
            </div>
            <div className="textfield">
              <TextField
                {...otpformik.getFieldProps("email")}
                fullWidth
                label="Email"
                id="fullWidth"
              />
            </div>
            <div className="textfield">
              <TextField
                {...otpformik.getFieldProps("mobile")}
                fullWidth
                label="Mobile No."
                id="fullWidth"
              />
            </div>
            <div className="textfield">
              <TextField
                {...otpformik.getFieldProps("password")}
                fullWidth
                label="Password"
                type="password"
                id="fullWidth"
              />
            </div>
            <div className="textfield">
              <TextField
                {...otpformik.getFieldProps("conpassword")}
                fullWidth
                label="Confirm Password"
                type="password"
                id="fullWidth"
              />
            </div>
          </div>
          <Button
            type="submit"
            fullWidth
            className="btn_In"
            variant="contained"
          >
            Get OTP
          </Button>
        </form>
        <p className="or" onClick={LogInModule}>
          ---Back to Log In
        </p>
      </div>

      <div className="homeThirdDiv" style={{ display: registerBlock }}>
        <p className="welcome">WELCOME - TO - LIBRARY</p>
        <form onSubmit={registerformik.handleSubmit}>
          <div className="homeFirstDiv_childFirstDiv">
            <div className="textfield">
              <p className="OtpNotice">
                OTP will be mail to Owner, only if he want you to register then
                he can share OTP with you !{" "}
              </p>
              <TextField
                {...registerformik.getFieldProps("otp")}
                fullWidth
                label="Verify OTP"
                id="fullWidth"
              />
            </div>
            <p className="or" onClick={OtpResend}>
              Resend OTP ?
            </p>
          </div>
          <Button
            type="submit"
            fullWidth
            className="btn_In"
            variant="contained"
            style={{ marginTop: "2%" }}
          >
            Register
          </Button>
        </form>
        <p className="or" onClick={LogInModule}>
          ---Back to Log In
        </p>
      </div>

      <div
        className="homeForgetPasswordDiv"
        style={{ display: forgetPassBlock }}
      >
        <p className="welcome">WELCOME - TO - LIBRARY</p>
        <form onSubmit={forgetPassformik.handleSubmit}>
          <div className="homeFirstDiv_childFirstDiv">
            <div className="textfield">
              <TextField
                {...forgetPassformik.getFieldProps("email")}
                fullWidth
                label="Enter Email"
                id="fullWidth"
              />
            </div>
          </div>
          <Button
            type="submit"
            fullWidth
            className="btn_In"
            variant="contained"
          >
            Send Link
          </Button>
        </form>
        <p className="or" onClick={LogInModule}>
          ---Back to Log In
        </p>
      </div>
    </div>
  );
};

export default Home;
