import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import studentPic from "../images/student_Image.avif";

const StudentById = () => {
  const [data, setData] = useState([]);
  const { id } = useParams();

  useEffect(() => {
    async function Res() {
      const result = await fetch("http://localhost:5555/students");
      const res = await result.json();
      setData(res.data);
    }

    Res();
  });

  return (
    <div>
      {data.map((ele, ind) => {
        if (ele._id === id) {
          return (
            <div>
              <div></div>
              <button className="btngreen">Edit</button>
              <button className="btnred">Delete</button>
            <div key={ind} className="studentbyid">
              <img src={studentPic} alt="Student Imgage" />
              <div className="studentbyidFlex">
                <div>
                  <h3>Student Name :</h3>
                  <p>{ele.studentName + " " + ele.lastName}</p>
                </div>
                <div>
                  <h3>Year ( Branch ):</h3>
                  <p>{ele.year + " " + "(" + ele.branch + ")"}</p>
                </div>
                <div>
                  <h3>Cell :</h3>
                  <p>{ele.cell}</p>
                </div>
                <div>
                  <h3>Email :</h3>
                  <p>{ele.email}</p>
                </div>
              </div>
              <div className="studentbyidFlex">
                <div>
                  <h3>Address :</h3>
                  <p>{ele.address}</p>
                </div>
                <div>
                  <h3>Father Name :</h3>
                  <p>{ele.fatherName}</p>
                </div>
                <div>
                  <h3>Mother Name :</h3>
                  <p>{ele.motherName}</p>
                </div>
                <div>
                  <h3>Parent Cell :</h3>
                  <p>{ele.parentCell}</p>
                </div>
              </div>
            </div>
            </div>
          );
        }
      })}

      <div>
        <table className="table">
          <tr>
            <th>Sr. No.</th>
            <th>Book Name</th>
            <th>Auther</th>
            <th>Edition</th>
            <th>Books Taken</th>
          </tr>
          <tr>
            <td>01</td>
            <td>Vishudhhi Marg</td>
            <td>Budhhaghosh</td>
            <td>1</td>
            <td>01</td>
          </tr>
          <tr>
            <td>02</td>
            <td>Buddha & His Dhamma</td>
            <td>Dr. B R Ambedkar</td>
            <td>5</td>
            <td>01</td>
          </tr>
        </table>
      </div>
    </div>
  );
};

export default StudentById;
