import React, { useState, useEffect } from "react";
import { useNavigate, useParams } from "react-router-dom";
import studentPic from "../images/student_Image.avif";
import TextField from "@mui/material/TextField";
import Button from "@mui/material/Button";
import SendIcon from "@mui/icons-material/Send";
import CloudUploadIcon from "@mui/icons-material/CloudUpload";
import studentForm from "../images/student_Form.jpeg";
import { styled } from "@mui/material/styles";

const StudentById = () => {
  const [data, setData] = useState([]);
  const { id } = useParams();
  const [file, setFile] = useState(studentPic);
  const [form, setForm] = useState(studentForm);
  const [editForm, setEditForm] = useState("none");
  const [displayDetails, setDisplayDetail] = useState("block");

  const navigate = useNavigate();

  useEffect(() => {

    async function Res() {
      const result = await fetch(`http://localhost:5555/students/${id}`);
      const res = await result.json();
      setData(res);
    }

    Res();
  });

  const moduleChange = () => {
    setEditForm("block");
    setDisplayDetail("none");
  };

  const reverseModule = () => {
    setEditForm("none");
    setDisplayDetail("block");
  };

  const DeleteStudent = () => {
    if (window.confirm("Are you want it to delete Permanently?")) {
      fetch(`http://localhost:3000/students/${id}`, {
        method: "DELETE",
      })
        .then((res) => res.json())
        .then((data) => console.log(data))
        .catch((error) => console.error(error));
    }
    navigate("/allstudents");
  };

  const VisuallyHiddenInput = styled("input")({
    clip: "rect(0 0 0 0)",
    clipPath: "inset(50%)",
    height: 1,
    overflow: "hidden",
    position: "absolute",
    bottom: 0,
    left: 0,
    whiteSpace: "nowrap",
    width: 1,
  });

  return (
    <div>
      <div style={{ display: editForm }} className="updateStudent">
        <div style={{ display: "flex", "marginBottom": "3%" }}>
          <Button
            onClick={reverseModule}
            style={{
              backgroundColor: "red",
              color: "blue",
              fontWeight: "700",
              padding: "5px",
              "marginLeft": "0px",
              "margin-right": "5%",
              border: "1px dotted yellow",
            }}
          >
            Cancel
          </Button>
          <h3>Update Students Details !</h3>
        </div>
        <div className="updateImgInfo">
          <form>
            <TextField
              // value={studentName}
              // onChange={handleStudentName}
              fullWidth
              label="Student Name"
              id="fullWidth"
              margin="normal"
            />
            <TextField
              // value={lastName}
              // onChange={handleLastName}
              fullWidth
              label="Last Name"
              id="fullWidth"
              margin="normal"
            />
