import React, { useState } from "react";
import Box from "@mui/material/Box";
import TextField from "@mui/material/TextField";
import Button from "@mui/material/Button";
import SendIcon from "@mui/icons-material/Send";
import CloudUploadIcon from "@mui/icons-material/CloudUpload";
import { styled } from "@mui/material/styles";
import logo from "../images/Book_Image.jpeg";
import { useNavigate } from "react-router-dom";

const AddBook = () => {
  const [file, setFile] = useState(logo);
  // const [bookName, setBookName] = useState("");
  // const [autherName, setAutherName] = useState("");
  // const [edition, setEdition] = useState("");
  // const [date, setDate] = useState("");
  // const [numberCopies, setNumberCopies] = useState("");
  const [data, setData] = useState({
    file: file,
    bookName: " ",
    autherName: " ",
    edition: " ",
    date: " ",
    numberCopies: " ",
  });

  function handleChange(e) {
    var reader = new FileReader();
    reader.readAsDataURL(e.target.files[0]);
    reader.onload = () =>{
      setFile(reader.result);
    }
    reader.onerror = (error) => {
      console.log(error);
    }
  }

  // function handleBookName(e) {
  //   setBookName(e.target.value);
  // }

  // function handleAutherName(e) {
  //   setAutherName(e.target.value);
  // }

  // function handleEdition(e) {
  //   setEdition(e.target.value);
  // }

  // function handleNumberCopies(e) {
  //   setNumberCopies(e.target.value);
  // }

  // function handleDate(e) {
  //   setDate(e.target.value);
  // }

  const navigate = useNavigate();

  let name, value;

  const handleInputs = (e) => {
    e.preventDefault();
    console.log(e.target.files)
    name = e.target.name;
    if (name !== "file") {
      value = e.target.value;
    } else {
      value = e.target.files[0];
    }

    setData({ ...data, [name]: value });
  };

  const NavigateToMainPage = async (e) => {
    e.preventDefault();
    console.log(data);

    if (
      !data.bookName ||
      !data.autherName ||
      !data.edition ||
      !data.date ||
      !data.numberCopies ||
      file === logo
    ) {
      setFile(logo);
      return alert(
        "Send all required field : Book Name, Auther Name, Edition, Date, Number Of Copies, File"
      );
    }

    // const Book = {
    //   bookName: bookName,
    //   autherName: autherName,
    //   edition: edition,
    //   date: date,
    //   numberCopies: numberCopies,
    //   file: file,
    //   bookOrStudent: 'Book',
    // };

    const requestOptions = {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(data),
    };

    fetch("http://localhost:5555/books/", requestOptions).then((response) =>
      response.json().then(alert("Book Added In Library Successfully!"))
    );

    // setFile(logo);
    // setBookName("");
    // setAutherName("");
    // setEdition("");
    // setNumberCopies("");
    // setDate("");
    // navigate("/allbooks");
  };

  const VisuallyHiddenInput = styled("input")({
    clip: "rect(0 0 0 0)",
    clipPath: "inset(50%)",
    height: 1,
    overflow: "hidden",
    position: "absolute",
    bottom: 0,
    left: 0,
    whiteSpace: "nowrap",
    width: 1,
  });

  return (
    <form className="addbook" encType="multipart/form-data">
      <Box
        sx={{
          width: 500,
          maxWidth: "100%",
          margin: 10,
        }}
      >
        <h3>Fill New Books Details Here!</h3>
        <TextField
          name="bookName"
          value={data.bookName}
          // onChange={handleBookName}
          onChange={handleInputs}
          fullWidth
          label="Book Name"
          id="fullWidth"
          margin="normal"
        />
        <TextField
          name="autherName"
          value={data.autherName}
          // onChange={handleAutherName}
          onChange={handleInputs}
          fullWidth
          label="Auther Name"
          id="fullWidth"
          margin="normal"
        />
        <TextField
          name="edition"
          value={data.edition}
          // onChange={handleEdition}
          onChange={handleInputs}
          fullWidth
          type="number"
          label="Edition"
          id="fullWidth"
          margin="normal"
        />
        <TextField
          name="numberCopies"
          value={data.numberCopies}
          // onChange={handleNumberCopies}
          onChange={handleInputs}
          fullWidth
          type="number"
          label="Number of Copies"
          id="fullWidth"
          margin="normal"
        />
        <TextField
          name="date"
          value={data.date}
          // onChange={handleDate}
          onChange={handleInputs}
          fullWidth
          type="date"
          label="Available In Library Date"
          id="fullWidth"
          margin="normal"
        />
        <Button
          name="file"
          value={data.file}
          type="file"
          onChange={handleChange}
          // onChange={handleInputs}
          component="label"
          variant="contained"
          color="success"
          startIcon={<CloudUploadIcon />}
        >
          Upload Image
          <VisuallyHiddenInput type="file" />
        </Button>
        <div className="addbookbtn">
          <Button
            onClick={NavigateToMainPage}
            type="submit"
            fullWidth
            className="btn_In"
            variant="contained"
            size="large"
            endIcon={<SendIcon />}
          >
            Add Book
          </Button>
        </div>
      </Box>
      <img src={file} className="bookImage" alt="book" />
    </form>
  );
};

export default AddBook;
