import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import studentPic from "../images/student_Image.avif";
import TextField from "@mui/material/TextField";
import Button from "@mui/material/Button";
import SendIcon from "@mui/icons-material/Send";
import CloudUploadIcon from "@mui/icons-material/CloudUpload";
import studentForm from "../images/student_Form.jpeg";
import { styled } from "@mui/material/styles";

const StudentById = () => {
  const [data, setData] = useState([]);
  const { id } = useParams();
  const [file, setFile] = useState(studentPic);
  const [form, setForm] = useState(studentForm);
  const [editForm, setEditForm] = useState("none");
  const [displayDetails, setDisplayDetail] = useState("block");

  useEffect(() => {
    async function Res() {
      const result = await fetch(`http://localhost:5555/students/$`);
      const res = await result.json();
      setData(res.data);
    }

    Res();
  });

  const moduleChange = () => {
    setEditForm("block");
    setDisplayDetail("none");
  };

  const reverseModule = () => {
    setEditForm("none");
    setDisplayDetail("block");
  };

  const VisuallyHiddenInput = styled("input")({
    clip: "rect(0 0 0 0)",
    clipPath: "inset(50%)",
    height: 1,
    overflow: "hidden",
    position: "absolute",
    bottom: 0,
    left: 0,
    whiteSpace: "nowrap",
    width: 1,
  });

  return (
    <div>
      <div style={{ display: editForm }} className="updateStudent">
        <div style={{ display: "flex", "margin-bottom": "3%" }}>
          <Button
            onClick={reverseModule}
            style={{
              backgroundColor: "red",
              color: "blue",
              fontWeight: "700",
              padding: "5px",
              "margin-left": "0px",
              "margin-right": "5%",
              border: "1px dotted yellow",
            }}
          >
            Cancel
          </Button>
          <h3>Update Students Details !</h3>
        </div>
        <div className="updateImgInfo">
          <form>
            <TextField
              // value={studentName}
              // onChange={handleStudentName}
              fullWidth
              label="Student Name"
              id="fullWidth"
              margin="normal"
            />
            <TextField
              // value={lastName}
              // onChange={handleLastName}
              fullWidth
              label="Last Name"
              id="fullWidth"
              margin="normal"
            />
            <TextField
              fullWidth
              // value={address}
              // onChange={handleAddress}
              label="Address"
              id="fullWidth"
              margin="normal"
            />
            <TextField
              fullWidth
              // value={mail}
              // onChange={handleEmail}
              label="Email"
              id="fullWidth"
              margin="normal"
            />
            <TextField
              fullWidth
              // value={cell}
              // onChange={handleCell}
              type="number"
              label="Mobile No."
              id="fullWidth"
              margin="normal"
            />
            <TextField
              fullWidth
              // value={joinDate}
              // onChange={handleDate}
              type="date"
              label="Date of Joining"
              id="fullWidth"
              margin="normal"
            />
            <TextField
              fullWidth
