import React from "react";
import { useEffect, useState } from "react";

const AllBooks = () => {
  const [data, setData] = useState("");

  useEffect(() => {
    async function Res() {
      const result = await fetch("http://localhost:5555/books");
      const res = await result.json();
      setData(res.data);
    }

    Res();
  });
  return (
    <div className="allbooks">
      <table className="table">
        <tr>
          <th>Sr. No.</th>
          <th>Book Name</th>
          <th>Auther Name</th>
          <th>Total Copies</th>
          <th>Available Copies</th>
          <th> More Info / Edit / Delete</th>
        </tr>
        {data.map((ele, ind) => {
          return (
            <tr>
              <td>{ind}</td>
              <td>{ele.bookName}</td>
              <td>{ele.autherName}</td>
              <td>15</td>
              <td>05</td>
              <td className="tdbtn">
                <button className="btnblue">Info</button>
                <button className="btngreen">Edit</button>
                <button className="btnred">Delete</button>
              </td>
            </tr>
          );
        })}
        {/* <tr>
          <td>02</td>
          <td>My Rule My Life</td>
          <td>Kunal Titare</td>
          <td>15</td>
          <td>05</td>
          <td className="tdbtn">
            <button className="btnblue">Info</button>
            <button className="btngreen">Edit</button>
            <button className="btnred">Delete</button>
          </td>
        </tr> */}
      </table>
    </div>
  );
};

export default AllBooks;
