import React, { useState } from "react";
import Box from "@mui/material/Box";
import TextField from "@mui/material/TextField";
import { useNavigate } from "react-router-dom";
import Button from "@mui/material/Button";
import { green } from '@mui/material/colors';
import Icon from '@mui/material/Icon';

const AddBook = () => {
  const [file, setFile] = useState();
  function handleChange(e) {
    console.log(e.target.files);
    setFile(URL.createObjectURL(e.target.files[0]));
  }

  const navigate = useNavigate();

  const NavigateToMainPage = () => {
    navigate("/allbooks");
  };

  return (
    <div className="addbook">
      <Box
        sx={{
          width: 500,
          maxWidth: "100%",
          margin: 10,
        }}
      >
        <h3>Fill New Books Details Here!</h3>
        <TextField fullWidth label="Book Name" id="fullWidth" margin="normal" />
        <TextField
          fullWidth
          label="Auther Name"
          id="fullWidth"
          margin="normal"
        />
        <TextField fullWidth label="Edition" id="fullWidth" margin="normal" />
        <TextField
          fullWidth
          type="number"
          label="Number of Copies"
          id="fullWidth"
          margin="normal"
        />
        <TextField
          fullWidth
          type="date"
          label="Available In Library Date"
          id="fullWidth"
          margin="normal"
        />
        <p>Add Image:</p>
        <input type="file" onChange={handleChange} />
        <Button
          onClick={NavigateToMainPage}
          fullWidth
          className="btn_In"
          variant="contained" endIcon='+'
        >
          Add Book
        </Button>
      </Box>
      <img src={file} className='bookImage'/>

    </div>
  );
};

export default AddBook;
