import React, { useState, useEffect } from "react";
import Box from "@mui/material/Box";
import TextField from "@mui/material/TextField";
import { useNavigate } from "react-router-dom";
import Button from "@mui/material/Button";
import SendIcon from "@mui/icons-material/Send";
import CloudUploadIcon from "@mui/icons-material/CloudUpload";
import { styled } from "@mui/material/styles";


const AddBook = () => {
  const [file, setFile] = useState('');
  const [bookName, setBookName] = useState("");
  const [autherName, setAutherName] = useState("");
  const [edition, setEdition] = useState("");
  const [date, setDate] = useState("");
  const [numberCopies, setNumberCopies] = useState("");
  const [book, setBook] = useState("");

  function handleChange(e) {
    console.log(e.target.files);
    setFile(URL.createObjectURL(e.target.files[0]));
  }

  function handleBookName(e) {
    setBookName(e.target.value);
  }

  function handleAutherName(e) {
    setAutherName(e.target.value);
  }

  function handleEdition(e) {
    setEdition(e.target.value);
  }

  function handleNumberCopies(e) {
    setNumberCopies(e.target.value);
  }

  function handleDate(e) {
    setDate(e.target.value);
  }

  // const navigate = useNavigate();

  const NavigateToMainPage = async (e) => {
    e.preventDefault();
    const Book = {
      bookName: bookName,
      autherName: autherName,
      edition: edition,
      date: date,
      numberCopies: numberCopies,
      file: file,
    };

    setBook(Book);
    console.log(Book);
  };

  useEffect(() => {
    const requestOptions = {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ book }),
    };
    fetch("http://localhost:5555/books/", requestOptions).then((response) =>
      response.json()
    );

    // empty dependency array means this effect will only run once (like componentDidMount in classes)
  }, [book]);

  const VisuallyHiddenInput = styled("input")({
    clip: "rect(0 0 0 0)",
    clipPath: "inset(50%)",
    height: 1,
    overflow: "hidden",
    position: "absolute",
    bottom: 0,
    left: 0,
    whiteSpace: "nowrap",
    width: 1,
  });

  return (
    <div className="addbook">
      <Box
        sx={{
          width: 500,
          maxWidth: "100%",
          margin: 10,
        }}
      >
        <h3>Fill New Books Details Here!</h3>
        <TextField
          value={bookName}
          onChange={handleBookName}
          fullWidth
          label="Book Name"
          id="fullWidth"
          margin="normal"
        />
        <TextField
          value={autherName}
          onChange={handleAutherName}
          fullWidth
          label="Auther Name"
          id="fullWidth"
          margin="normal"
        />
        <TextField
          value={edition}
          onChange={handleEdition}
          fullWidth
          label="Edition"
          id="fullWidth"
          margin="normal"
        />
        <TextField
          value={numberCopies}
          onChange={handleNumberCopies}
          fullWidth
          type="number"
          label="Number of Copies"
          id="fullWidth"
          margin="normal"
        />
        <TextField
          value={date}
          onChange={handleDate}
          fullWidth
          type="date"
          label="Available In Library Date"
          id="fullWidth"
          margin="normal"
        />
        <Button
          onChange={handleChange}
          component="label"
          variant="contained"
          color="success"
          startIcon={<CloudUploadIcon />}
        >
          Upload Image
          <VisuallyHiddenInput type="file" />
        </Button>
        <div className="addbookbtn">
          <Button
            onClick={NavigateToMainPage}
            fullWidth
            className="btn_In"
            variant="contained"
            size="large"
            endIcon={<SendIcon />}
          >
            Add Book
          </Button>
        </div>
      </Box>
      <img src={file} className="bookImage" alt="book" />
    </div>
  );
};

export default AddBook;
