import React from "react";
import { useEffect, useState } from "react";

const AllBooks = () => {
  const [data, setData] = useState("");

  useEffect(() => {
    async function Res() {
      const result = await fetch("http://localhost:5555/books");
      const res = await result.json();
      setData(res.count);
    }

    Res();
  });
  return (
    <div className="allbooks">
      <table className="table">
        <tr>
          <th>Sr. No.</th>
          <th>Book Name</th>
          <th>Auther Name</th>
          <th>Total Copies</th>
          <th>Available Copies</th>
          <th>Edit / More Info </th>
        </tr>
        <tr>
          <td>01</td>
          <td>My Rule My Life</td>
          <td>Kunal Titare</td>
          <td>15</td>
          <td>05</td>
          <td className="tdbtn">
            <button className="btngreen">Edit</button>
            <button className="btnred">Delete</button>
            <button className="btnblue">Info</button>
          </td>
        </tr>
        <tr>
          <td>01</td>
          <td>My Rule My Life</td>
          <td>Kunal Titare</td>
          <td>15</td>
          <td>05</td>
          <td className="tdbtn">
            <button className="btngreen">Edit</button>
            <button className="btnred">Delete</button>
            <button className="btnblue">Info</button>
          </td>
        </tr>
      </table>
    </div>
  );
};

export default AllBooks;
