import React, { useState } from "react";
import Box from "@mui/material/Box";
import TextField from "@mui/material/TextField";
import Button from "@mui/material/Button";
import SendIcon from "@mui/icons-material/Send";
import CloudUploadIcon from "@mui/icons-material/CloudUpload";
import { styled } from "@mui/material/styles";
import studentPic from "../images/student_Image.avif";
import studentForm from "../images/student_Form.jpeg";
import { useNavigate } from "react-router-dom";


const NewStudent = () => {
  const [file, setFile] = useState(studentPic);
  const [form, setForm] = useState(studentForm);
  // const [studentName, setStudentName] = useState("");
  // const [address, setAddress] = useState("");
  // const [cell, setCell] = useState("");
  // const [branch, setBranch] = useState("");
  // const [mail, setEmail] = useState("");
  // const [year, setYear] = useState("");
  // const [motherName, setMotherName] = useState("");
  // const [fatherName, setFatherName] = useState("");
  // const [parentCell, setParentCell] = useState("");
  // const [lastName, setLastName] = useState("");
  // const [joinDate, setDate] = useState("");
  const [data, setData] = useState({
    file: file,
    form:form,
    studentName: " ",
    address: " ",
    cell: " ",
    branch: " ",
    mail: " ",
    mail: " ",
    year: " ",
    motherName: " ",
    fatherName: " ",
    parentCell: " ",
    lastName: " ",
    joinDate: " ",
  })

  const navigate = useNavigate();


  function handleChange(e) {
    var reader = new FileReader();
    reader.readAsDataURL(e.target.files[0]);
    reader.onload = () =>{
      setFile(reader.result);
    }
    reader.onerror = (error) => {
      console.log(error);
    }
  }

  function handleFormChange(e) {
    var reader = new FileReader();
    reader.readAsDataURL(e.target.files[0]);
    reader.onload = () =>{
      setForm(reader.result);
    }
    reader.onerror = (error) => {
      console.log(error);
    }
  }


let name, value;

  // function handleStudentName(e) {
  //   setStudentName(e.target.value);
  // }

  // function handleAddress(e) {
  //   setAddress(e.target.value);
  // }

  // function handleCell(e) {
  //   setCell(e.target.value);
  // }

  // function handleBranch(e) {
  //   setBranch(e.target.value);
  // }

  // function handleEmail(e) {
  //   setEmail(e.target.value);
  // }

  // function handleYear(e) {
  //   setYear(e.target.value);
  // }

  // function handleMotherName(e) {
  //   setMotherName(e.target.value);
  // }

  // function handleFatherName(e) {
  //   setFatherName(e.target.value);
  // }

  // function handleParentCell(e) {
  //   setParentCell(e.target.value);
  // }

  // function handleLastName(e) {
  //   setLastName(e.target.value);
  // }

  // function handleDate(e) {
  //   setDate(e.target.value);
  // }


  const NavigateToMainPage = async (e) => {
    e.preventDefault();

    if (
      !data.studentName ||
      !data.address ||
      !data.cell ||
      !data.branch ||
      !data.mail ||
      !data.year ||
      !data.motherName ||
      !data.fatherName ||
      !data.parentCell ||
      !data.lastName ||
      file === studentPic ||
      form === studentForm
    ) {
      setFile(studentPic);
      setForm(studentForm);
      return alert(
        "Fill all required field"
      );
    }

    // const Student = {
    //   studentName: studentName,
    //   address: address,
    //   cell: cell,
    //   branch: branch,
    //   email: mail,
    //   year: year,
    //   motherName: motherName,
    //   fatherName: fatherName,
    //   parentCell: parentCell,
    //   lastName: lastName,
    //   studentImg: studentPic,
    //   studentForm: studentForm,
    //   dateOfJoining: joinDate,
    // };

    const requestOptions = {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(data),
    };

    fetch("http://localhost:5555/students/", requestOptions).then((response) =>
      response.json().then(alert("Student Added In Library Successfully!"))
    );

    // setFile(studentPic);
    // setForm(studentForm);
    // setStudentName("");
    // setAddress("");
    // setCell("");
    // setBranch("");
    // setEmail("");
    // setYear("");
    // setFatherName("");
    // setMotherName("");
    // setParentCell("");
    // setLastName("");
    // setDate("");

    setData({
      file: file,
      form:form,
      studentName: " ",
      address: " ",
      cell: " ",
      branch: " ",
      mail: " ",
      mail: " ",
      year: " ",
      motherName: " ",
      fatherName: " ",
      parentCell: " ",
      lastName: " ",
      joinDate: " ",
    })

    navigate('/allstudents')
  };

  const VisuallyHiddenInput = styled("input")({
    clip: "rect(0 0 0 0)",
    clipPath: "inset(50%)",
    height: 1,
    overflow: "hidden",
    position: "absolute",
    bottom: 0,
    left: 0,
    whiteSpace: "nowrap",
    width: 1,
  });
  return (
    <form encType="multipart/form-data">
    <div className="newStudent">
      <Box
        sx={{
          width: 500,
          maxWidth: "100%",
          margin: 10,
        }}
      >
        <h3>New Students Details !</h3>
        <TextField
          value={data.studentName}
          onChange={handleInputs}
          fullWidth
          label="Student Name"
          id="fullWidth"
          margin="normal"
        />
        <TextField
          value={data.lastName}
          onChange={handleInputs}
          fullWidth
          label="Last Name"
          id="fullWidth"
          margin="normal"
        />
        <TextField fullWidth  value={data.address} onChange={handleInputs} label="Address" id="fullWidth" margin="normal" />
        <TextField fullWidth  value={data.mail} onChange={handleInputs} label="Email" id="fullWidth" margin="normal" />
        <TextField
          fullWidth
          value={cell}
          onChange={handleCell}
          type="number"
          label="Mobile No."
          id="fullWidth"
          margin="normal"
        />
        <TextField
          fullWidth
          value={data.joinDate}
          onChange={handleInputs}
          type="date"
          label="Date of Joining"
          id="fullWidth"
          margin="normal"
        />
        <TextField fullWidth  value={data.branch} onChange={handleInputs} label="Branch" id="fullWidth" margin="normal" />
        <TextField
          fullWidth 
          value={data.year}
          onChange={handleInputs}
          type="number"
          label="Year"
          id="fullWidth"
          margin="normal"
        />
        <TextField
          fullWidth
          value={data.fatherName}
          onChange={handleInputs}
          label="Father Name"
          id="fullWidth"
          margin="normal"
        />
        <TextField
          fullWidth
          value={data.motherName}
          onChange={handleInputs}
          label="Mother Name"
          id="fullWidth"
          margin="normal"
        />
        <TextField
          fullWidth
          value={data.parentCell}
          onChange={handleInputs}
          type="number"
          label="Parents Contact No."
          id="fullWidth"
          margin="normal"
        />
        <div className="studentImgBtnDiv">
          <Button
            onChange={handleChange}
            name = "image"
            component="label"
            variant="contained"
            color="success"
            startIcon={<CloudUploadIcon />}
          >
            Upload Student Image
            <VisuallyHiddenInput type="file" />
          </Button>

          <Button
            onChange={handleFormChange}
            name = "form"
            component="label"
            variant="contained"
            color="success"
            startIcon={<CloudUploadIcon />}
          >
            Upload Form
            <VisuallyHiddenInput type="file" />
          </Button>
        </div>
        <div className="addbookbtn">
          <Button
            onClick={NavigateToMainPage}
            fullWidth
            className="btn_In"
            variant="contained"
            size="large"
            endIcon={<SendIcon />}
          >
            Add Student
          </Button>
        </div>
      </Box>
      <div className="studentImg">
        <img src={file} className="studentImage" alt="student" />
        <img src={form} className="formImage" alt="form" />
      </div>
    </div>
    </form>
  );
};

export default NewStudent;
