import React, { useState, useEffect } from "react";
import Box from "@mui/material/Box";
import TextField from "@mui/material/TextField";
import Button from "@mui/material/Button";
import SendIcon from "@mui/icons-material/Send";
import CloudUploadIcon from "@mui/icons-material/CloudUpload";
import { styled } from "@mui/material/styles";
import studentPic from "../images/student_Image.avif";
import studentForm from "../images/student_Form.jpeg";
import { useNavigate } from "react-router-dom";
import { useParams } from "react-router-dom";

// data.map((ele, ind) => {
//   if (ele._id === id) {
//     setStudentName(ele.studentName);
//     setAddress(ele.address);
//     setCell(ele.cell);
//     setBranch(ele.branch);
//     setEmail(ele.email);
//     setYear(ele.year);
//     setMotherName(ele.motherName);
//     setFatherName(ele.fatherName);
//     setParentCell(ele.parentCell);
//     setLastName(ele.lastName);
//     setDate(ele.dateOfJoining);
//   }
// })

const UpdateStudent = () => {
  const [data, setData] = useState([]);
  const { id } = useParams();

  useEffect(() => {
    async function Res() {
      const result = await fetch("http://localhost:5555/students");
      const res = await result.json();
      setData(
        res.data.map((ele, ind) => {
          if (ele._id === id) {
            return ele;
          }
        })
      );
    }

    Res();
  }, []);

  const [file, setFile] = useState(studentPic);
  const [form, setForm] = useState(studentForm);
  const [studentName, setStudentName] = useState(`${data.studentName}`);
  const [address, setAddress] = useState(data.address);
  const [cell, setCell] = useState("");
  const [branch, setBranch] = useState("");
  const [mail, setEmail] = useState("");
  const [year, setYear] = useState("");
  const [motherName, setMotherName] = useState("");
  const [fatherName, setFatherName] = useState("");
  const [parentCell, setParentCell] = useState("");
  const [lastName, setLastName] = useState("");
  const [joinDate, setDate] = useState("");

  function handleChange(e) {
    var reader = new FileReader();
    reader.readAsDataURL(e.target.files[0]);
    reader.onload = () => {
      setFile(reader.result);
    };
    reader.onerror = (error) => {
      console.log(error);
    };
  }

  function handleFormChange(e) {
    var reader = new FileReader();
    reader.readAsDataURL(e.target.files[0]);
    reader.onload = () => {
      setForm(reader.result);
    };
    reader.onerror = (error) => {
      console.log(error);
    };
  }

  function handleStudentName(e) {
    setStudentName(e.target.value);
  }

  function handleAddress(e) {
    setAddress(e.target.value);
  }

  function handleCell(e) {
    setCell(e.target.value);
  }

  function handleBranch(e) {
    setBranch(e.target.value);
  }

  function handleEmail(e) {
    setEmail(e.target.value);
  }

  function handleYear(e) {
    setYear(e.target.value);
  }

  function handleMotherName(e) {
    setMotherName(e.target.value);
  }

  function handleFatherName(e) {
    setFatherName(e.target.value);
  }

  function handleParentCell(e) {
    setParentCell(e.target.value);
  }

  function handleLastName(e) {
    setLastName(e.target.value);
  }

  function handleDate(e) {
    setDate(e.target.value);
  }

  const navigate = useNavigate;

  const NavigateToMainPage = async (e) => {
    e.preventDefault();

    if (
      !studentName ||
      !address ||
      !cell ||
      !branch ||
      !mail ||
      !year ||
      !motherName ||
      !fatherName ||
      !parentCell ||
      !lastName ||
      file === studentPic ||
      form === studentForm
    ) {
      setFile(studentPic);
      setForm(studentForm);
      return alert("Fill all required field");
    }

    const Student = {
      studentName: studentName,
      address: address,
      cell: cell,
      branch: branch,
      email: mail,
      year: year,
      motherName: motherName,
      fatherName: fatherName,
      parentCell: parentCell,
      lastName: lastName,
      studentImg: file,
      studentForm: form,
      bookOrStudent: "Book",
      dateOfJoining: joinDate,
    };

    const requestOptions = {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(Student),
    };

    fetch("http://localhost:5555/students/", requestOptions).then((response) =>
      response.json().then(alert("Student Added In Library Successfully!"))
    );

    setFile(studentPic);
    setForm(studentForm);
    setStudentName("");
    setAddress("");
    setCell("");
    setBranch("");
    setEmail("");
    setYear("");
    setFatherName("");
    setMotherName("");
    setParentCell("");
    setLastName("");
    setDate("");

    navigate("/allstudents");
  };

  const VisuallyHiddenInput = styled("input")({
    clip: "rect(0 0 0 0)",
    clipPath: "inset(50%)",
    height: 1,
    overflow: "hidden",
    position: "absolute",
    bottom: 0,
    left: 0,
    whiteSpace: "nowrap",
    width: 1,
  });

  return (
    <div className="updateStudent">
      <h3>Update Students Details !</h3>
      <div className="updateImgInfo">
        <form>
          <TextField
            value={studentName}
            onChange={handleStudentName}
            fullWidth
            label="Student Name"
            id="fullWidth"
            margin="normal"
          />
          <TextField
            value={lastName}
            onChange={handleLastName}
            fullWidth
            label="Last Name"
            id="fullWidth"
            margin="normal"
          />
          <TextField
            fullWidth
            value={address}
            onChange={handleAddress}
            label="Address"
            id="fullWidth"
            margin="normal"
          />
          <TextField
            fullWidth
            value={mail}
            onChange={handleEmail}
            label="Email"
            id="fullWidth"
            margin="normal"
          />
          <TextField
            fullWidth
            value={cell}
            onChange={handleCell}
            type="number"
            label="Mobile No."
            id="fullWidth"
            margin="normal"
          />
          <TextField
            fullWidth
            value={joinDate}
            onChange={handleDate}
            type="date"
            label="Date of Joining"
            id="fullWidth"
            margin="normal"
          />
          <TextField
            fullWidth
            value={branch}
            onChange={handleBranch}
            label="Branch"
            id="fullWidth"
            margin="normal"
          />
          <TextField
            fullWidth
            value={year}
            onChange={handleYear}
            type="number"
            label="Year"
            id="fullWidth"
            margin="normal"
          />
          <TextField
            fullWidth
            value={fatherName}
            onChange={handleFatherName}
            label="Father Name"
            id="fullWidth"
            margin="normal"
          />
          <TextField
            fullWidth
            value={motherName}
            onChange={handleMotherName}
            label="Mother Name"
            id="fullWidth"
            margin="normal"
          />
          <TextField
            fullWidth
            value={parentCell}
            onChange={handleParentCell}
            type="number"
            label="Parents Contact No."
            id="fullWidth"
            margin="normal"
          />
          <div className="studentImgBtnDiv">
            <Button
              onChange={handleChange}
              component="label"
              variant="contained"
              color="success"
              startIcon={<CloudUploadIcon />}
            >
              Upload Student Image
              <VisuallyHiddenInput type="file" />
            </Button>

            <Button
              onChange={handleFormChange}
              component="label"
              variant="contained"
              color="success"
              startIcon={<CloudUploadIcon />}
            >
              Upload Form
              <VisuallyHiddenInput type="file" />
            </Button>
          </div>
          <div className="addbookbtn">
            <Button
              onClick={NavigateToMainPage}
              fullWidth
              className="btn_In"
              variant="contained"
              size="large"
              endIcon={<SendIcon />}
            >
              Add Student
            </Button>
          </div>
        </form>
        <div className="studentImg">
          <img src={file} className="updateStudentImage" alt="student" />
          <img src={form} className="updateormImage" alt="form" />
        </div>
      </div>
    </div>
  );
};

export default UpdateStudent;
